1: using System.ComponentModel;
2: using System.Text.Json.Serialization;
3: using YamlDotNet.Serialization;
4: 
5: namespace CycoDev.CustomTools.Models
6: {
7:     /// <summary>
8:     /// Represents a custom tool definition that can be loaded from YAML.
9:     /// </summary>
10:     public class CustomToolDefinition
11:     {
12:         /// <summary>
13:         /// Gets or sets the name of the tool.
14:         /// </summary>
15:         [YamlMember(Alias = "name")]
16:         public string Name { get; set; } = string.Empty;
17: 
18:         /// <summary>
19:         /// Gets or sets the description of the tool.
20:         /// </summary>
21:         [YamlMember(Alias = "description")]
22:         public string Description { get; set; } = string.Empty;
23: 
24:         /// <summary>
25:         /// Gets or sets the version of the tool.
26:         /// </summary>
27:         [YamlMember(Alias = "version")]
28:         public string Version { get; set; } = "1.0.0";
29: 
30:         /// <summary>
31:         /// Gets or sets the minimum CYCOD version required.
32:         /// </summary>
33:         [YamlMember(Alias = "min-cycod-version")]
34:         public string MinCycodVersion { get; set; } = "1.0.0";
35: 
36:         /// <summary>
37:         /// Gets or sets the Bash command to execute.
38:         /// </summary>
39:         [YamlMember(Alias = "bash")]
40:         public string? BashCommand { get; set; }
41: 
42:         /// <summary>
43:         /// Gets or sets the Windows CMD command to execute.
44:         /// </summary>
45:         [YamlMember(Alias = "cmd")]
46:         public string? CmdCommand { get; set; }
47: 
48:         /// <summary>
49:         /// Gets or sets the PowerShell command to execute.
50:         /// </summary>
51:         [YamlMember(Alias = "pwsh")]
52:         public string? PowerShellCommand { get; set; }
53: 
54:         /// <summary>
55:         /// Gets or sets the direct command to execute.
56:         /// </summary>
57:         [YamlMember(Alias = "run")]
58:         public string? RunCommand { get; set; }
59: 
60:         /// <summary>
61:         /// Gets or sets the script content.
62:         /// </summary>
63:         [YamlMember(Alias = "script")]
64:         public string? Script { get; set; }
65: 
66:         /// <summary>
67:         /// Gets or sets the shell to use with the script.
68:         /// </summary>
69:         [YamlMember(Alias = "shell")]
70:         public string? Shell { get; set; }
71: 
72:         /// <summary>
73:         /// Gets or sets the steps for multi-step tools.
74:         /// </summary>
75:         [YamlMember(Alias = "steps")]
76:         public List<CustomToolStep>? Steps { get; set; }
77: 
78:         /// <summary>
79:         /// Gets or sets the parameters for the tool.
80:         /// </summary>
81:         [YamlMember(Alias = "parameters")]
82:         public Dictionary<string, CustomToolParameter> Parameters { get; set; } = new();
83: 
84:         /// <summary>
85:         /// Gets or sets the timeout in milliseconds.
86:         /// </summary>
87:         [YamlMember(Alias = "timeout")]
88:         public int Timeout { get; set; } = 60000;
89: 
90:         /// <summary>
91:         /// Gets or sets the working directory.
92:         /// </summary>
93:         [YamlMember(Alias = "working-directory")]
94:         public string? WorkingDirectory { get; set; }
95: 
96:         /// <summary>
97:         /// Gets or sets the platforms supported by this tool.
98:         /// </summary>
99:         [YamlMember(Alias = "platforms")]
100:         public List<string> Platforms { get; set; } = new();
101: 
102:         /// <summary>
103:         /// Gets or sets the tags for categorization and security.
104:         /// </summary>
105:         [YamlMember(Alias = "tags")]
106:         public List<string> Tags { get; set; } = new();
107: 
108:         /// <summary>
109:         /// Gets or sets whether to ignore errors.
110:         /// </summary>
111:         [YamlMember(Alias = "ignore-errors")]
112:         public bool IgnoreErrors { get; set; } = false;
113: 
114:         /// <summary>
115:         /// Gets or sets the data to pass via stdin.
116:         /// </summary>
117:         [YamlMember(Alias = "input")]
118:         public string? Input { get; set; }
119: 
120:         /// <summary>
121:         /// Gets or sets environment variables for the tool.
122:         /// </summary>
123:         [YamlMember(Alias = "environment")]
124:         public CustomToolEnvironment? Environment { get; set; }
125: 
126:         /// <summary>
127:         /// Gets or sets function calling schema configuration.
128:         /// </summary>
129:         [YamlMember(Alias = "function-calling")]
130:         public CustomToolFunctionCalling? FunctionCalling { get; set; }
131: 
132:         /// <summary>
133:         /// Gets or sets security configuration for the tool.
134:         /// </summary>
135:         [YamlMember(Alias = "security")]
136:         public CustomToolSecurity? Security { get; set; }
137: 
138:         /// <summary>
139:         /// Gets or sets file path configuration for the tool.
140:         /// </summary>
141:         [YamlMember(Alias = "file-paths")]
142:         public CustomToolFilePaths? FilePaths { get; set; }
143: 
144:         /// <summary>
145:         /// Gets or sets metadata for categorization and discovery.
146:         /// </summary>
147:         [YamlMember(Alias = "metadata")]
148:         public CustomToolMetadata? Metadata { get; set; }
149: 
150:         /// <summary>
151:         /// Gets or sets tests for the tool.
152:         /// </summary>
153:         [YamlMember(Alias = "tests")]
154:         public List<CustomToolTest>? Tests { get; set; }
155: 
156:         /// <summary>
157:         /// Gets or sets resource constraints for the tool.
158:         /// </summary>
159:         [YamlMember(Alias = "resources")]
160:         public CustomToolResources? Resources { get; set; }
161: 
162:         /// <summary>
163:         /// Gets or sets whether this tool is interactive.
164:         /// </summary>
165:         [YamlMember(Alias = "interactive")]
166:         public bool Interactive { get; set; } = false;
167: 
168:         /// <summary>
169:         /// Gets or sets interactive options for the tool.
170:         /// </summary>
171:         [YamlMember(Alias = "interactive-options")]
172:         public CustomToolInteractiveOptions? InteractiveOptions { get; set; }
173: 
174:         /// <summary>
175:         /// Gets or sets whether this tool is an alias.
176:         /// </summary>
177:         [YamlMember(Alias = "type")]
178:         public string? Type { get; set; }
179: 
180:         /// <summary>
181:         /// Gets or sets the base tool for an alias.
182:         /// </summary>
183:         [YamlMember(Alias = "base-tool")]
184:         public string? BaseTool { get; set; }
185: 
186:         /// <summary>
187:         /// Gets or sets default parameters for an alias.
188:         /// </summary>
189:         [YamlMember(Alias = "default-parameters")]
190:         public Dictionary<string, string>? DefaultParameters { get; set; }
191: 
192:         /// <summary>
193:         /// Gets or sets the changelog for the tool.
194:         /// </summary>
195:         [YamlMember(Alias = "changelog")]
196:         public List<CustomToolChangelogEntry>? Changelog { get; set; }
197: 
198:         /// <summary>
199:         /// Validates the tool definition.
200:         /// </summary>
201:         /// <param name="errorMessage">The error message if validation fails.</param>
202:         /// <returns>True if valid, false otherwise.</returns>
203:         public bool Validate(out string? errorMessage)
204:         {
205:             errorMessage = null;
206: 
207:             // Validate required fields
208:             if (string.IsNullOrEmpty(Name))
209:             {
210:                 errorMessage = "Tool name is required.";
211:                 return false;
212:             }
213: 
214:             if (string.IsNullOrEmpty(Description))
215:             {
216:                 errorMessage = "Tool description is required.";
217:                 return false;
218:             }
219: 
220:             // Validate that at least one command type is specified
221:             bool hasCommand = !string.IsNullOrEmpty(BashCommand) ||
222:                               !string.IsNullOrEmpty(CmdCommand) ||
223:                               !string.IsNullOrEmpty(PowerShellCommand) ||
224:                               !string.IsNullOrEmpty(RunCommand) ||
225:                               !string.IsNullOrEmpty(Script) ||
226:                               (Steps != null && Steps.Count > 0);
227: 
228:             if (!hasCommand)
229:             {
230:                 errorMessage = "Tool must have at least one command or steps.";
231:                 return false;
232:             }
233: 
234:             // If script is specified, shell must also be specified
235:             if (!string.IsNullOrEmpty(Script) && string.IsNullOrEmpty(Shell))
236:             {
237:                 errorMessage = "Shell must be specified when using a script.";
238:                 return false;
239:             }
240: 
241:             // Validate steps
242:             if (Steps != null)
243:             {
244:                 foreach (var step in Steps)
245:                 {
246:                     if (string.IsNullOrEmpty(step.Name))
247:                     {
248:                         errorMessage = "All steps must have a name.";
249:                         return false;
250:                     }
251: 
252:                     bool hasStepCommand = !string.IsNullOrEmpty(step.BashCommand) ||
253:                                          !string.IsNullOrEmpty(step.CmdCommand) ||
254:                                          !string.IsNullOrEmpty(step.PowerShellCommand) ||
255:                                          !string.IsNullOrEmpty(step.RunCommand) ||
256:                                          !string.IsNullOrEmpty(step.UseTool);
257: 
258:                     if (!hasStepCommand)
259:                     {
260:                         errorMessage = $"Step '{step.Name}' must have a command.";
261:                         return false;
262:                     }
263: 
264:                     // If using another tool, validate that the with parameters match
265:                     if (!string.IsNullOrEmpty(step.UseTool) && (step.With == null || step.With.Count == 0))
266:                     {
267:                         // This is just a warning, not an error
268:                         Console.WriteLine($"Warning: Step '{step.Name}' uses tool '{step.UseTool}' but doesn't provide any parameters.");
269:                     }
270:                 }
271:             }
272: 
273:             // Check for duplicate step names
274:             if (Steps != null)
275:             {
276:                 var stepNames = new HashSet<string>();
277:                 foreach (var step in Steps)
278:                 {
279:                     if (!stepNames.Add(step.Name))
280:                     {
281:                         errorMessage = $"Duplicate step name: '{step.Name}'.";
282:                         return false;
283:                     }
284:                 }
285:             }
286: 
287:             // Set parameter names and validate parameters
288:             foreach (var param in Parameters)
289:             {
290:                 param.Value.Name = param.Key;
291: 
292:                 if (string.IsNullOrEmpty(param.Value.Description))
293:                 {
294:                     errorMessage = $"Parameter '{param.Key}' must have a description.";
295:                     return false;
296:                 }
297: 
298:                 // Validate parameter type
299:                 string paramType = param.Value.Type.ToLowerInvariant();
300:                 if (paramType != "string" && paramType != "number" && paramType != "boolean" && paramType != "array" && paramType != "object")
301:                 {
302:                     errorMessage = $"Parameter '{param.Key}' has invalid type: '{param.Value.Type}'. Must be one of: string, number, boolean, array, object.";
303:                     return false;
304:                 }
305: 
306:                 // Validate default value against parameter type if provided
307:                 if (param.Value.Default != null)
308:                 {
309:                     if (!param.Value.Validate(param.Value.Default, out string? paramError))
310:                     {
311:                         errorMessage = paramError;
312:                         return false;
313:                     }
314:                 }
315:             }
316: 
317:             // Validate parameter references in commands
318:             HashSet<string> missingParams = new HashSet<string>();
319: 
320:             // Check parameters in main commands
321:             CheckParameterReferences(BashCommand, missingParams);
322:             CheckParameterReferences(CmdCommand, missingParams);
323:             CheckParameterReferences(PowerShellCommand, missingParams);
324:             CheckParameterReferences(RunCommand, missingParams);
325:             CheckParameterReferences(Script, missingParams);
326: 
327:             // Check parameters in step commands
328:             if (Steps != null)
329:             {
330:                 foreach (var step in Steps)
331:                 {
332:                     CheckParameterReferences(step.BashCommand, missingParams);
333:                     CheckParameterReferences(step.CmdCommand, missingParams);
334:                     CheckParameterReferences(step.PowerShellCommand, missingParams);
335:                     CheckParameterReferences(step.RunCommand, missingParams);
336:                 }
337:             }
338: 
339:             // Check for missing parameters
340:             if (missingParams.Count > 0)
341:             {
342:                 errorMessage = $"The following parameters are referenced but not defined: {string.Join(", ", missingParams)}";
343:                 return false;
344:             }
345: 
346:             return true;
347:         }
348: 
349:         private void CheckParameterReferences(string? command, HashSet<string> missingParams)
350:         {
351:             if (string.IsNullOrEmpty(command))
352:             {
353:                 return;
354:             }
355: 
356:             // Find parameter references like {PARAM_NAME}
357:             var matches = System.Text.RegularExpressions.Regex.Matches(command, @"\{([^}]+)\}");
358:             foreach (System.Text.RegularExpressions.Match match in matches)
359:             {
360:                 string paramName = match.Groups[1].Value;
361: 
362:                 // Skip step references
363:                 if (paramName.Contains("."))
364:                 {
365:                     continue;
366:                 }
367: 
368:                 // Skip parameter exists check for environment variables and other special parameters
369:                 if (paramName.StartsWith("ENV_") || 
370:                     paramName == "WORKSPACE" || 
371:                     paramName == "TOOL_NAME" || 
372:                     paramName == "INPUT_PARAM")
373:                 {
374:                     continue;
375:                 }
376: 
377:                 // Check if parameter is defined
378:                 if (!Parameters.ContainsKey(paramName))
379:                 {
380:                     missingParams.Add(paramName);
381:                 }
382:             }
383:         }
384:     }
385: 
386:     /// <summary>
387:     /// Represents a parameter for a custom tool.
388:     /// </summary>
389:     public class CustomToolParameter
390:     {
391:         /// <summary>
392:         /// Gets or sets the type of the parameter.
393:         /// </summary>
394:         [YamlMember(Alias = "type")]
395:         public string Type { get; set; } = "string";
396: 
397:         /// <summary>
398:         /// Gets or sets the description of the parameter.
399:         /// </summary>
400:         [YamlMember(Alias = "description")]
401:         public string Description { get; set; } = string.Empty;
402: 
403:         /// <summary>
404:         /// Gets or sets whether the parameter is required.
405:         /// </summary>
406:         [YamlMember(Alias = "required")]
407:         public bool Required { get; set; } = false;
408: 
409:         /// <summary>
410:         /// Gets or sets the default value of the parameter.
411:         /// </summary>
412:         [YamlMember(Alias = "default")]
413:         public object? Default { get; set; }
414: 
415:         /// <summary>
416:         /// Gets or sets validation rules for the parameter.
417:         /// </summary>
418:         [YamlMember(Alias = "validation")]
419:         public Dictionary<string, object>? Validation { get; set; }
420: 
421:         /// <summary>
422:         /// Gets or sets the transform function to apply to the parameter.
423:         /// </summary>
424:         [YamlMember(Alias = "transform")]
425:         public string? Transform { get; set; }
426: 
427:         /// <summary>
428:         /// Gets or sets the format string for the parameter.
429:         /// </summary>
430:         [YamlMember(Alias = "format")]
431:         public string? Format { get; set; }
432: 
433:         /// <summary>
434:         /// Gets or sets examples for the parameter.
435:         /// </summary>
436:         [YamlMember(Alias = "examples")]
437:         public List<string>? Examples { get; set; }
438: 
439:         /// <summary>
440:         /// Gets or sets detailed help text for the parameter.
441:         /// </summary>
442:         [YamlMember(Alias = "detailed-help")]
443:         public string? DetailedHelp { get; set; }
444: 
445:         /// <summary>
446:         /// Gets or sets security options for the parameter.
447:         /// </summary>
448:         [YamlMember(Alias = "security")]
449:         public CustomToolParameterSecurity? Security { get; set; }
450: 
451:         /// <summary>
452:         /// Gets or sets the name of the parameter.
453:         /// </summary>
454:         [YamlIgnore]
455:         public string Name { get; set; } = string.Empty;
456: 
457:         /// <summary>
458:         /// Validates a parameter value against the parameter definition.
459:         /// </summary>
460:         /// <param name="value">The value to validate.</param>
461:         /// <param name="errorMessage">The error message if validation fails.</param>
462:         /// <returns>True if validation passes, false otherwise.</returns>
463:         public bool Validate(object? value, out string? errorMessage)
464:         {
465:             errorMessage = null;
466: 
467:             // Check if required
468:             if (Required && value == null)
469:             {
470:                 errorMessage = $"Parameter '{Name}' is required.";
471:                 return false;
472:             }
473: 
474:             // If null and not required, validation passes
475:             if (value == null)
476:             {
477:                 return true;
478:             }
479: 
480:             // Validate by type
481:             switch (Type?.ToLowerInvariant())
482:             {
483:                 case "string":
484:                     return ValidateString(value.ToString(), out errorMessage);
485:                 case "number":
486:                     return ValidateNumber(value, out errorMessage);
487:                 case "boolean":
488:                     return ValidateBoolean(value, out errorMessage);
489:                 case "array":
490:                     return ValidateArray(value, out errorMessage);
491:                 case "object":
492:                     return ValidateObject(value, out errorMessage);
493:                 default:
494:                     // Default to string validation if type is not specified
495:                     return ValidateString(value.ToString(), out errorMessage);
496:             }
497:         }
498: 
499:         private bool ValidateString(string? value, out string? errorMessage)
500:         {
501:             errorMessage = null;
502: 
503:             if (value == null)
504:             {
505:                 return true;
506:             }
507: 
508:             if (Validation != null)
509:             {
510:                 // Validate string length
511:                 if (Validation.ContainsKey("minLength"))
512:                 {
513:                     if (int.TryParse(Validation["minLength"].ToString(), out int minLength))
514:                     {
515:                         if (value.Length < minLength)
516:                         {
517:                             errorMessage = $"Parameter '{Name}' must be at least {minLength} characters long.";
518:                             return false;
519:                         }
520:                     }
521:                 }
522: 
523:                 if (Validation.ContainsKey("maxLength"))
524:                 {
525:                     if (int.TryParse(Validation["maxLength"].ToString(), out int maxLength))
526:                     {
527:                         if (value.Length > maxLength)
528:                         {
529:                             errorMessage = $"Parameter '{Name}' must be no more than {maxLength} characters long.";
530:                             return false;
531:                         }
532:                     }
533:                 }
534: 
535:                 // Validate string pattern
536:                 if (Validation.ContainsKey("pattern"))
537:                 {
538:                     string pattern = Validation["pattern"].ToString();
539:                     try
540:                     {
541:                         if (!System.Text.RegularExpressions.Regex.IsMatch(value, pattern))
542:                         {
543:                             errorMessage = $"Parameter '{Name}' does not match the required pattern: {pattern}.";
544:                             return false;
545:                         }
546:                     }
547:                     catch (Exception ex)
548:                     {
549:                         errorMessage = $"Invalid pattern for parameter '{Name}': {ex.Message}";
550:                         return false;
551:                     }
552:                 }
553: 
554:                 // Validate enum values
555:                 if (Validation.ContainsKey("enum"))
556:                 {
557:                     if (Validation["enum"] is List<object> enumValues)
558:                     {
559:                         bool found = false;
560:                         foreach (var enumValue in enumValues)
561:                         {
562:                             if (value.Equals(enumValue.ToString(), StringComparison.OrdinalIgnoreCase))
563:                             {
564:                                 found = true;
565:                                 break;
566:                             }
567:                         }
568: 
569:                         if (!found)
570:                         {
571:                             errorMessage = $"Parameter '{Name}' must be one of: {string.Join(", ", enumValues)}.";
572:                             return false;
573:                         }
574:                     }
575:                 }
576:             }
577: 
578:             return true;
579:         }
580: 
581:         private bool ValidateNumber(object value, out string? errorMessage)
582:         {
583:             errorMessage = null;
584: 
585:             // Try to convert to decimal for validation
586:             if (!decimal.TryParse(value.ToString(), out decimal numericValue))
587:             {
588:                 errorMessage = $"Parameter '{Name}' must be a valid number.";
589:                 return false;
590:             }
591: 
592:             if (Validation != null)
593:             {
594:                 // Validate minimum value
595:                 if (Validation.ContainsKey("minimum"))
596:                 {
597:                     if (decimal.TryParse(Validation["minimum"].ToString(), out decimal minimum))
598:                     {
599:                         if (numericValue < minimum)
600:                         {
601:                             errorMessage = $"Parameter '{Name}' must be at least {minimum}.";
602:                             return false;
603:                         }
604:                     }
605:                 }
606: 
607:                 // Validate maximum value
608:                 if (Validation.ContainsKey("maximum"))
609:                 {
610:                     if (decimal.TryParse(Validation["maximum"].ToString(), out decimal maximum))
611:                     {
612:                         if (numericValue > maximum)
613:                         {
614:                             errorMessage = $"Parameter '{Name}' must be no more than {maximum}.";
615:                             return false;
616:                         }
617:                     }
618:                 }
619: 
620:                 // Validate multiple of
621:                 if (Validation.ContainsKey("multipleOf"))
622:                 {
623:                     if (decimal.TryParse(Validation["multipleOf"].ToString(), out decimal multipleOf))
624:                     {
625:                         if (multipleOf != 0 && numericValue % multipleOf != 0)
626:                         {
627:                             errorMessage = $"Parameter '{Name}' must be a multiple of {multipleOf}.";
628:                             return false;
629:                         }
630:                     }
631:                 }
632:             }
633: 
634:             return true;
635:         }
636: 
637:         private bool ValidateBoolean(object value, out string? errorMessage)
638:         {
639:             errorMessage = null;
640: 
641:             // Try to convert to boolean for validation
642:             string strValue = value.ToString().ToLowerInvariant();
643:             if (strValue != "true" && strValue != "false" && 
644:                 strValue != "1" && strValue != "0" &&
645:                 strValue != "yes" && strValue != "no")
646:             {
647:                 errorMessage = $"Parameter '{Name}' must be a valid boolean (true/false, 1/0, yes/no).";
648:                 return false;
649:             }
650: 
651:             return true;
652:         }
653: 
654:         private bool ValidateArray(object value, out string? errorMessage)
655:         {
656:             errorMessage = null;
657: 
658:             // Check if the value is actually an array
659:             if (!(value is System.Collections.IEnumerable enumerable) || value is string)
660:             {
661:                 errorMessage = $"Parameter '{Name}' must be an array.";
662:                 return false;
663:             }
664: 
665:             if (Validation != null)
666:             {
667:                 // Count items for validation
668:                 int count = 0;
669:                 foreach (var _ in enumerable)
670:                 {
671:                     count++;
672:                 }
673: 
674:                 // Validate minimum items
675:                 if (Validation.ContainsKey("minItems"))
676:                 {
677:                     if (int.TryParse(Validation["minItems"].ToString(), out int minItems))
678:                     {
679:                         if (count < minItems)
680:                         {
681:                             errorMessage = $"Parameter '{Name}' must have at least {minItems} items.";
682:                             return false;
683:                         }
684:                     }
685:                 }
686: 
687:                 // Validate maximum items
688:                 if (Validation.ContainsKey("maxItems"))
689:                 {
690:                     if (int.TryParse(Validation["maxItems"].ToString(), out int maxItems))
691:                     {
692:                         if (count > maxItems)
693:                         {
694:                             errorMessage = $"Parameter '{Name}' must have no more than {maxItems} items.";
695:                             return false;
696:                         }
697:                     }
698:                 }
699: 
700:                 // Validate unique items
701:                 if (Validation.ContainsKey("uniqueItems"))
702:                 {
703:                     if (bool.TryParse(Validation["uniqueItems"].ToString(), out bool uniqueItems) && uniqueItems)
704:                     {
705:                         var items = new HashSet<string>();
706:                         foreach (var item in enumerable)
707:                         {
708:                             string itemStr = item?.ToString() ?? string.Empty;
709:                             if (!items.Add(itemStr))
710:                             {
711:                                 errorMessage = $"Parameter '{Name}' must have unique items.";
712:                                 return false;
713:                             }
714:                         }
715:                     }
716:                 }
717:             }
718: 
719:             return true;
720:         }
721: 
722:         private bool ValidateObject(object value, out string? errorMessage)
723:         {
724:             errorMessage = null;
725: 
726:             // For now, basic validation that it's a dictionary or similar
727:             if (!(value is System.Collections.IDictionary))
728:             {
729:                 errorMessage = $"Parameter '{Name}' must be an object.";
730:                 return false;
731:             }
732: 
733:             // Additional object validation could be added here
