737:         /// <summary>
738:         /// Transforms a parameter value according to the transformation rules.
739:         /// </summary>
740:         /// <param name="value">The value to transform.</param>
741:         /// <returns>The transformed value.</returns>
742:         public object? TransformValue(object? value)
743:         {
744:             if (value == null || string.IsNullOrEmpty(Transform))
745:             {
746:                 return value;
747:             }
748: 
749:             string strValue = value.ToString();
750: 
751:             // Apply transformation based on the transform expression
752:             switch (Transform.ToLowerInvariant())
753:             {
754:                 case "lowercase":
755:                 case "tolowercase":
756:                 case "tolower":
757:                     return strValue.ToLowerInvariant();
758: 
759:                 case "uppercase":
760:                 case "touppercase":
761:                 case "toupper":
762:                     return strValue.ToUpperInvariant();
763: 
764:                 case "trim":
765:                     return strValue.Trim();
766: 
767:                 case "int":
768:                 case "integer":
769:                 case "floor":
770:                 case "math.floor":
771:                     if (decimal.TryParse(strValue, out decimal decValue))
772:                     {
773:                         return Math.Floor(decValue);
774:                     }
775:                     return value;
776: 
777:                 case "ceil":
778:                 case "ceiling":
779:                 case "math.ceiling":
780:                 case "math.ceil":
781:                     if (decimal.TryParse(strValue, out decimal ceilValue))
782:                     {
783:                         return Math.Ceiling(ceilValue);
784:                     }
785:                     return value;
786: 
787:                 case "round":
788:                 case "math.round":
789:                     if (decimal.TryParse(strValue, out decimal roundValue))
790:                     {
791:                         return Math.Round(roundValue);
792:                     }
793:                     return value;
794: 
795:                 case "boolean":
796:                 case "bool":
797:                     string loweredValue = strValue.ToLowerInvariant();
798:                     return loweredValue == "true" || loweredValue == "1" || loweredValue == "yes";
799: 
800:                 default:
801:                     // If it's a custom transformation, return as is for now
802:                     // A more sophisticated transformation system could be implemented later
803:                     return value;
804:             }
805:         }
806:     }
