45:         {
46:             if (string.IsNullOrEmpty(tool.BaseTool))
47:             {
48:                 return new CustomToolExecutionResult
49:                 {
50:                     ExitCode = 1,
51:                     Output = "Error: Alias tool does not specify a base tool.",
52:                     Error = "Alias tool does not specify a base tool.",
53:                     IsError = true
54:                 };
55:             }
56:             
57:             // Find the base tool (this would typically be done through the factory)
58:             // For this implementation, we'll assume the base tool is passed in somehow
59:             // In a real implementation, we'd need to modify the API to pass in the tool factory
60:             
61:             // Merge parameters: default parameters from alias with provided parameters
62:             var mergedParameters = new Dictionary<string, object?>();
63:             
64:             // First add default parameters from the alias
65:             if (tool.DefaultParameters != null)
66:             {
67:                 foreach (var param in tool.DefaultParameters)
68:                 {
69:                     mergedParameters[param.Key] = param.Value;
70:                 }
71:             }
72:             
73:             // Then override with provided parameters
74:             foreach (var param in parameters)
