104:                 if (step.WaitFor != null && step.WaitFor.Count > 0)
105:                 {
106:                     var stepsToWaitFor = step.WaitFor.Where(s => runningTasks.ContainsKey(s)).ToList();
107:                     if (stepsToWaitFor.Count > 0)
108:                     {
109:                         ConsoleHelpers.WriteDebugLine($"Waiting for steps to complete: {string.Join(", ", stepsToWaitFor)}");
110:                         
111:                         foreach (var waitStep in stepsToWaitFor)
112:                         {
113:                             if (runningTasks.TryGetValue(waitStep, out var task))
114:                             {
115:                                 var result = await task;
116:                                 stepResults[waitStep] = result;
117:                                 runningTasks.Remove(waitStep);
118:                                 
119:                                 // Log the completed step
120:                                 finalOutput.AppendLine($"--- Step: {waitStep} ---");
121:                                 finalOutput.AppendLine(result.Output);
122:                                 finalOutput.AppendLine();
123:                                 
124:                                 if (!string.IsNullOrEmpty(result.Error))
125:                                 {
126:                                     finalError.AppendLine($"--- Step: {waitStep} ---");
127:                                     finalError.AppendLine(result.Error);
128:                                     finalError.AppendLine();
129:                                 }
130:                                 
131:                                 // Set final exit code to the first non-zero exit code
132:                                 if (finalExitCode == 0 && result.ExitCode != 0)
133:                                 {
134:                                     finalExitCode = result.ExitCode;
135:                                 }
136:                                 
137:                                 // Check if we should continue after error
138:                                 if (result.ExitCode != 0 && !tool.IgnoreErrors)
139:                                 {
140:                                     // Look up the corresponding step to check its continue-on-error setting
141:                                     var stepDefinition = tool.Steps.FirstOrDefault(s => s.Name == waitStep);
142:                                     if (stepDefinition != null && !stepDefinition.ContinueOnError)
143:                                     {
144:                                         // Stop execution if the step failed and continue-on-error is false
145:                                         return new CustomToolExecutionResult
146:                                         {
147:                                             ExitCode = finalExitCode,
148:                                             Output = finalOutput.ToString().TrimEnd(),
149:                                             Error = finalError.ToString().TrimEnd(),
150:                                             IsError = true,
151:                                             StepResults = stepResults
152:                                         };
153:                                     }
154:                                 }
155:                             }
156:                         }
157:                     }
158:                 }
159:                 
160:                 // Check if we should run this step based on run-condition
161:                 if (!string.IsNullOrEmpty(step.RunCondition))
162:                 {
163:                     bool shouldRun = EvaluateRunCondition(step.RunCondition, stepResults);
164:                     if (!shouldRun)
165:                     {
166:                         ConsoleHelpers.WriteDebugLine($"Skipping step '{step.Name}' because run condition evaluated to false");
167:                         continue;
168:                     }
169:                 }
170:                 
171:                 // Execute the step
172:                 var stepTask = ExecuteStepAsync(step, parameters, stepResults, tool);
173:                 
174:                 if (step.Parallel)
175:                 {
176:                     // Add to running tasks if parallel
177:                     ConsoleHelpers.WriteDebugLine($"Starting step '{step.Name}' in parallel");
178:                     runningTasks[step.Name] = stepTask;
179:                 }
180:                 else
181:                 {
182:                     // Wait for completion if not parallel
183:                     var stepResult = await stepTask;
184:                     stepResults[step.Name] = stepResult;
185:                     
186:                     // Append step output to final output
187:                     finalOutput.AppendLine($"--- Step: {step.Name} ---");
188:                     finalOutput.AppendLine(stepResult.Output);
189:                     finalOutput.AppendLine();
190:                     
191:                     // Append step error to final error if there is any
192:                     if (!string.IsNullOrEmpty(stepResult.Error))
193:                     {
194:                         finalError.AppendLine($"--- Step: {step.Name} ---");
195:                         finalError.AppendLine(stepResult.Error);
196:                         finalError.AppendLine();
197:                     }
198:                     
199:                     // Set final exit code to the first non-zero exit code
200:                     if (finalExitCode == 0 && stepResult.ExitCode != 0)
201:                     {
202:                         finalExitCode = stepResult.ExitCode;
203:                     }
204:                     
205:                     // Check if we should continue after error
206:                     if (stepResult.ExitCode != 0 && !step.ContinueOnError && !tool.IgnoreErrors)
207:                     {
208:                         break;
209:                     }
210:                 }
211:             }
212:             
213:             // Wait for any remaining parallel tasks
214:             if (runningTasks.Count > 0)
215:             {
216:                 ConsoleHelpers.WriteDebugLine($"Waiting for {runningTasks.Count} parallel steps to complete");
217:                 
218:                 foreach (var task in runningTasks)
219:                 {
220:                     var stepName = task.Key;
221:                     var stepResult = await task.Value;
222:                     stepResults[stepName] = stepResult;
223:                     
224:                     // Append step output to final output
225:                     finalOutput.AppendLine($"--- Step: {stepName} ---");
226:                     finalOutput.AppendLine(stepResult.Output);
227:                     finalOutput.AppendLine();
228:                     
229:                     // Append step error to final error if there is any
230:                     if (!string.IsNullOrEmpty(stepResult.Error))
231:                     {
232:                         finalError.AppendLine($"--- Step: {stepName} ---");
233:                         finalError.AppendLine(stepResult.Error);
234:                         finalError.AppendLine();
235:                     }
236:                     
237:                     // Set final exit code to the first non-zero exit code
238:                     if (finalExitCode == 0 && stepResult.ExitCode != 0)
239:                     {
240:                         finalExitCode = stepResult.ExitCode;
241:                     }
242:                 }
243:             }
244:             
245:             return new CustomToolExecutionResult
246:             {
247:                 ExitCode = finalExitCode,
248:                 Output = finalOutput.ToString().TrimEnd(),
249:                 Error = finalError.ToString().TrimEnd(),
250:                 IsError = finalExitCode != 0,
251:                 StepResults = stepResults
252:             };
253:         }
