256:         {
257:             string? command = null;
258:             ShellType shellType = ShellType.Bash;
259: 
260:             if (!string.IsNullOrEmpty(tool.BashCommand))
261:             {
262:                 command = tool.BashCommand;
263:                 shellType = ShellType.Bash;
264:             }
265:             else if (!string.IsNullOrEmpty(tool.CmdCommand))
266:             {
267:                 command = tool.CmdCommand;
268:                 shellType = ShellType.Cmd;
269:             }
270:             else if (!string.IsNullOrEmpty(tool.PowerShellCommand))
271:             {
272:                 command = tool.PowerShellCommand;
273:                 shellType = ShellType.PowerShell;
274:             }
275:             else if (!string.IsNullOrEmpty(tool.RunCommand))
276:             {
277:                 command = tool.RunCommand;
278:                 shellType = ShellType.Run;
279:             }
280:             else if (!string.IsNullOrEmpty(tool.Script))
281:             {
282:                 command = tool.Script;
283:                 shellType = !string.IsNullOrEmpty(tool.Shell) 
284:                     ? ParseShellType(tool.Shell) 
285:                     : ShellType.Bash;
286:             }
287: 
288:             if (string.IsNullOrEmpty(command))
289:             {
290:                 return new CustomToolExecutionResult
291:                 {
292:                     ExitCode = 1,
293:                     Output = "Error: No command specified for tool.",
294:                     Error = "No command specified for tool.",
295:                     IsError = true
296:                 };
297:             }
298: 
299:             // Substitute parameters
