306:         private async Task<CustomToolExecutionResult> ExecuteStepAsync(
307:             CustomToolStep step, 
308:             Dictionary<string, object?> parameters,
309:             Dictionary<string, CustomToolExecutionResult> previousStepResults,
310:             CustomToolDefinition tool)
311:         {
312:             int attempts = 1;
313:             int delay = 0;
314:             string? fallbackCommand = null;
315:             
316:             // Configure retry and fallback if error handling is defined
317:             if (step.ErrorHandling != null)
318:             {
319:                 if (step.ErrorHandling.Retry != null)
320:                 {
321:                     attempts = step.ErrorHandling.Retry.Attempts;
322:                     delay = step.ErrorHandling.Retry.Delay;
323:                 }
324:                 
325:                 fallbackCommand = step.ErrorHandling.Fallback;
326:             }
327:             
328:             for (int attempt = 1; attempt <= attempts; attempt++)
329:             {
330:                 string? command = null;
331:                 ShellType shellType = ShellType.Bash;
332: 
333:                 if (!string.IsNullOrEmpty(step.BashCommand))
334:                 {
335:                     command = step.BashCommand;
336:                     shellType = ShellType.Bash;
337:                 }
338:                 else if (!string.IsNullOrEmpty(step.CmdCommand))
339:                 {
340:                     command = step.CmdCommand;
341:                     shellType = ShellType.Cmd;
342:                 }
343:                 else if (!string.IsNullOrEmpty(step.PowerShellCommand))
344:                 {
345:                     command = step.PowerShellCommand;
346:                     shellType = ShellType.PowerShell;
347:                 }
348:                 else if (!string.IsNullOrEmpty(step.RunCommand))
349:                 {
350:                     command = step.RunCommand;
351:                     shellType = ShellType.Run;
352:                 }
353:                 else if (!string.IsNullOrEmpty(step.UseTool))
354:                 {
355:                     // Implementation for using another tool
356:                     var toolParameters = new Dictionary<string, object?>();
357:                     
358:                     // Add parameters from the 'with' section
359:                     if (step.With != null)
360:                     {
361:                         foreach (var param in step.With)
362:                         {
363:                             // Substitute any step references or parameters
364:                             string value = SubstituteParameters(param.Value, parameters, previousStepResults);
365:                             toolParameters[param.Key] = value;
366:                         }
367:                     }
368:                     
369:                     // For now, return a simulated result
370:                     // In a real implementation, we'd need access to the tool factory to look up the tool
371:                     return new CustomToolExecutionResult
372:                     {
373:                         ExitCode = 0,
374:                         Output = $"Step '{step.Name}' would execute tool '{step.UseTool}' with parameters: {string.Join(", ", toolParameters.Select(p => $"{p.Key}={p.Value}"))}",
375:                         Error = string.Empty,
376:                         IsError = false
377:                     };
378:                 }
379: 
380:                 if (string.IsNullOrEmpty(command))
381:                 {
382:                     return new CustomToolExecutionResult
383:                     {
384:                         ExitCode = 1,
385:                         Output = $"Error: No command specified for step '{step.Name}'.",
386:                         Error = $"No command specified for step '{step.Name}'.",
387:                         IsError = true
388:                     };
389:                 }
390: 
391:                 // Substitute parameters and previous step outputs
392:                 command = SubstituteParameters(command, parameters, previousStepResults);
393: 
394:                 // Execute the command
395:                 var result = await ExecuteCommandAsync(command, shellType, tool);
396:                 
397:                 // Check if execution succeeded or if we should retry
398:                 if (result.ExitCode == 0 || attempt >= attempts)
399:                 {
400:                     // Success or final attempt
401:                     // If it failed and there's a fallback, try that
402:                     if (result.ExitCode != 0 && !string.IsNullOrEmpty(fallbackCommand))
403:                     {
404:                         ConsoleHelpers.WriteDebugLine($"Step '{step.Name}' failed after {attempt} attempts, trying fallback command");
405:                         
406:                         // Substitute parameters in fallback command
407:                         var processedFallback = SubstituteParameters(fallbackCommand, parameters, previousStepResults);
408:                         
409:                         // Execute fallback command using the same shell type
410:                         return await ExecuteCommandAsync(processedFallback, shellType, tool);
411:                     }
412:                     
413:                     return result;
414:                 }
415:                 
416:                 // Retry logic
417:                 ConsoleHelpers.WriteDebugLine($"Step '{step.Name}' failed (exit code {result.ExitCode}), retrying ({attempt}/{attempts})");
418:                 
419:                 if (delay > 0 && attempt < attempts)
420:                 {
421:                     await Task.Delay(delay);
422:                 }
423:             }
424:             
425:             // This should never be reached due to the return in the loop
426:             return new CustomToolExecutionResult
427:             {
428:                 ExitCode = 1,
429:                 Output = $"Error: Failed to execute step '{step.Name}' after {attempts} attempts.",
430:                 Error = $"Failed to execute step '{step.Name}' after {attempts} attempts.",
431:                 IsError = true
432:             };
433:         }
