435:         private async Task<CustomToolExecutionResult> ExecuteCommandAsync(string command, ShellType shellType, CustomToolDefinition? tool = null)
436:         {
437:             try
438:             {
439:                 // Prepare the process start info based on shell type
440:                 var processStartInfo = new System.Diagnostics.ProcessStartInfo();
441:                 
442:                 switch (shellType)
443:                 {
444:                     case ShellType.Bash:
445:                         processStartInfo.FileName = GetBashPath();
446:                         processStartInfo.Arguments = $"-c \"{EscapeForShell(command)}\"";
447:                         break;
448:                         
449:                     case ShellType.Cmd:
450:                         processStartInfo.FileName = "cmd.exe";
451:                         processStartInfo.Arguments = $"/c {command}";
452:                         break;
453:                         
454:                     case ShellType.PowerShell:
455:                         processStartInfo.FileName = "powershell.exe";
456:                         processStartInfo.Arguments = $"-Command \"{EscapeForPowerShell(command)}\"";
457:                         break;
458:                         
459:                     case ShellType.Run:
460:                         // Extract the command and arguments
461:                         var parts = command.Split(' ', 2);
462:                         processStartInfo.FileName = parts[0];
463:                         if (parts.Length > 1)
464:                         {
465:                             processStartInfo.Arguments = parts[1];
466:                         }
467:                         break;
468:                 }
469:                 
470:                 // Configure process
471:                 processStartInfo.RedirectStandardOutput = true;
472:                 processStartInfo.RedirectStandardError = true;
473:                 processStartInfo.UseShellExecute = false;
474:                 processStartInfo.CreateNoWindow = true;
475:                 
476:                 // Set working directory if specified
477:                 if (tool != null && !string.IsNullOrEmpty(tool.WorkingDirectory))
478:                 {
479:                     processStartInfo.WorkingDirectory = NormalizeWorkingDirectory(tool.WorkingDirectory);
480:                 }
481:                 
482:                 // Setup environment variables if specified
483:                 if (tool?.Resources?.EnvironmentVariables != null)
484:                 {
485:                     foreach (var envVar in tool.Resources.EnvironmentVariables)
486:                     {
487:                         processStartInfo.EnvironmentVariables[envVar.Key] = envVar.Value;
488:                     }
489:                 }
490:                 
491:                 // Inherit parent environment if specified
492:                 processStartInfo.EnvironmentVariables["CYCOD_TOOL_NAME"] = tool?.Name ?? "unnamed-tool";
493:                 
494:                 // Start the process
495:                 using var process = new System.Diagnostics.Process();
496:                 process.StartInfo = processStartInfo;
497:                 
498:                 var outputBuilder = new StringBuilder();
499:                 var errorBuilder = new StringBuilder();
500:                 
501:                 // Set up output and error handling
502:                 process.OutputDataReceived += (sender, e) => 
503:                 {
504:                     if (e.Data != null)
505:                     {
506:                         outputBuilder.AppendLine(e.Data);
507:                         
508:                         // Stream output if specified
509:                         if (tool?.Resources?.Streaming ?? false)
510:                         {
511:                             ConsoleHelpers.WriteDebugLine(e.Data);
512:                         }
513:                     }
514:                 };
515:                 
516:                 process.ErrorDataReceived += (sender, e) => 
517:                 {
518:                     if (e.Data != null)
519:                     {
520:                         errorBuilder.AppendLine(e.Data);
521:                         
522:                         // Stream error if specified
523:                         if (tool?.Resources?.Streaming ?? false)
524:                         {
525:                             ConsoleHelpers.WriteErrorLine(e.Data);
526:                         }
527:                     }
528:                 };
529:                 
530:                 // Start process and begin reading output
531:                 process.Start();
532:                 process.BeginOutputReadLine();
533:                 process.BeginErrorReadLine();
534:                 
535:                 // Wait for process to exit with timeout
536:                 int timeoutMs = tool?.Timeout ?? 60000; // Default 60 seconds
537:                 var waitForExitTask = process.WaitForExitAsync();
538:                 var timeoutTask = Task.Delay(timeoutMs);
539:                 
540:                 var completedTask = await Task.WhenAny(waitForExitTask, timeoutTask);
541:                 
542:                 if (completedTask == timeoutTask)
543:                 {
544:                     // Process timed out
545:                     try
546:                     {
547:                         process.Kill(true);
548:                     }
549:                     catch
550:                     {
551:                         // Ignore errors killing the process
552:                     }
553:                     
554:                     return new CustomToolExecutionResult
555:                     {
556:                         ExitCode = -1,
557:                         Output = outputBuilder.ToString().TrimEnd(),
558:                         Error = $"Process timed out after {timeoutMs}ms.",
559:                         IsError = true
560:                     };
561:                 }
562:                 
563:                 // Process completed within timeout
564:                 await waitForExitTask;
565:                 
566:                 // Check if output exceeds buffer limit
567:                 if (tool?.Resources?.MaxSize != null && 
568:                     outputBuilder.Length > tool.Resources.MaxSize)
569:                 {
570:                     if (tool.Resources.Truncation)
571:                     {
572:                         // Truncate output if specified
573:                         outputBuilder.Length = tool.Resources.MaxSize.Value;
574:                         outputBuilder.AppendLine("\n... output truncated due to size limit ...");
575:                     }
576:                 }
577:                 
578:                 return new CustomToolExecutionResult
579:                 {
580:                     ExitCode = process.ExitCode,
581:                     Output = outputBuilder.ToString().TrimEnd(),
582:                     Error = errorBuilder.ToString().TrimEnd(),
583:                     IsError = process.ExitCode != 0
584:                 };
585:             }
586:             catch (Exception ex)
587:             {
588:                 return new CustomToolExecutionResult
589:                 {
590:                     ExitCode = 1,
591:                     Output = $"Error executing command: {ex.Message}",
592:                     Error = ex.ToString(),
593:                     IsError = true
594:                 };
595:             }
596:         }
