598:         /// <summary>
599:         /// Gets the path to the bash executable.
600:         /// </summary>
601:         /// <returns>The path to bash.</returns>
602:         private string GetBashPath()
603:         {
604:             if (OperatingSystem.IsWindows())
605:             {
606:                 // Try Git Bash first
607:                 var gitBashPath = Path.Combine(
608:                     Environment.GetFolderPath(Environment.SpecialFolder.ProgramFiles),
609:                     "Git",
610:                     "bin",
611:                     "bash.exe");
612:                     
613:                 if (File.Exists(gitBashPath))
614:                 {
615:                     return gitBashPath;
616:                 }
617:                 
618:                 // Try Windows Subsystem for Linux
619:                 if (File.Exists(@"C:\Windows\System32\bash.exe"))
620:                 {
621:                     return @"C:\Windows\System32\bash.exe";
622:                 }
623:                 
624:                 // Default to cmd as fallback
625:                 return "cmd.exe";
626:             }
627:             
628:             return "/bin/bash";
629:         }
