631:         /// <summary>
632:         /// Escapes a command for shell execution.
633:         /// </summary>
634:         /// <param name="command">The command to escape.</param>
635:         /// <returns>The escaped command.</returns>
636:         private string EscapeForShell(string command)
637:         {
