651:         /// <summary>
652:         /// Evaluates a run condition to determine if a step should be executed.
653:         /// </summary>
654:         /// <param name="condition">The condition string.</param>
655:         /// <param name="stepResults">Results from previous steps.</param>
656:         /// <returns>The evaluation result.</returns>
657:         private bool EvaluateRunCondition(string condition, Dictionary<string, CustomToolExecutionResult> stepResults)
658:         {
659:             // Substitute step references
660:             string evaluatableCondition = SubstituteStepReferences(condition, stepResults);
661:             
662:             // Parse and evaluate the condition
663:             try
664:             {
665:                 // Handle simple exit code conditions
666:                 if (evaluatableCondition.Contains("==") || evaluatableCondition.Contains("!="))
667:                 {
668:                     // Split by comparison operator
669:                     string[] parts;
670:                     bool isEqual;
671:                     
672:                     if (evaluatableCondition.Contains("=="))
673:                     {
674:                         parts = evaluatableCondition.Split("==", 2);
675:                         isEqual = true;
676:                     }
677:                     else
678:                     {
679:                         parts = evaluatableCondition.Split("!=", 2);
680:                         isEqual = false;
681:                     }
682:                     
683:                     if (parts.Length == 2)
684:                     {
685:                         string left = parts[0].Trim();
686:                         string right = parts[1].Trim();
687:                         
688:                         // Try to convert to integers for numeric comparison
689:                         int leftValue = 0, rightValue = 0;
690:                         bool isNumeric = int.TryParse(left, out leftValue) && 
691:                                         int.TryParse(right, out rightValue);
692:                                         
693:                         if (isNumeric)
694:                         {
695:                             return isEqual ? leftValue == rightValue : leftValue != rightValue;
696:                         }
697:                         else
698:                         {
699:                             // String comparison
700:                             return isEqual ? left == right : left != right;
701:                         }
702:                     }
703:                 }
704:                 // Handle logical operators
705:                 else if (evaluatableCondition.Contains("&&") || evaluatableCondition.Contains("||"))
706:                 {
707:                     // For now, implement simple AND/OR logic
708:                     if (evaluatableCondition.Contains("&&"))
709:                     {
710:                         string[] parts = evaluatableCondition.Split("&&");
711:                         return parts.All(p => EvaluateSimpleCondition(p.Trim()));
712:                     }
713:                     else
714:                     {
715:                         string[] parts = evaluatableCondition.Split("||");
716:                         return parts.Any(p => EvaluateSimpleCondition(p.Trim()));
717:                     }
718:                 }
719:                 // Handle greater/less than
720:                 else if (evaluatableCondition.Contains(">") || evaluatableCondition.Contains("<"))
721:                 {
722:                     string[] parts;
723:                     string op;
724:                     
725:                     if (evaluatableCondition.Contains(">="))
726:                     {
727:                         parts = evaluatableCondition.Split(">=", 2);
728:                         op = ">=";
729:                     }
730:                     else if (evaluatableCondition.Contains("<="))
731:                     {
732:                         parts = evaluatableCondition.Split("<=", 2);
733:                         op = "<=";
734:                     }
735:                     else if (evaluatableCondition.Contains(">"))
736:                     {
737:                         parts = evaluatableCondition.Split(">", 2);
738:                         op = ">";
739:                     }
740:                     else
741:                     {
742:                         parts = evaluatableCondition.Split("<", 2);
743:                         op = "<";
744:                     }
745:                     
746:                     if (parts.Length == 2)
747:                     {
748:                         string left = parts[0].Trim();
749:                         string right = parts[1].Trim();
750:                         
751:                         // Try to convert to numbers for comparison
752:                         if (decimal.TryParse(left, out decimal leftValue) && 
753:                             decimal.TryParse(right, out decimal rightValue))
754:                         {
755:                             return op switch
756:                             {
757:                                 ">=" => leftValue >= rightValue,
758:                                 "<=" => leftValue <= rightValue,
759:                                 ">" => leftValue > rightValue,
760:                                 "<" => leftValue < rightValue,
761:                                 _ => false
762:                             };
763:                         }
764:                     }
765:                 }
766:                 
767:                 // Default to simple boolean evaluation
768:                 return EvaluateSimpleCondition(evaluatableCondition);
769:             }
770:             catch (Exception ex)
771:             {
772:                 ConsoleHelpers.WriteDebugLine($"Error evaluating condition: {ex.Message}");
773:                 return false;
774:             }
775:         }
776:         
777:         /// <summary>
778:         /// Evaluates a simple condition.
779:         /// </summary>
780:         /// <param name="condition">The simple condition string.</param>
781:         /// <returns>The evaluation result.</returns>
782:         private bool EvaluateSimpleCondition(string condition)
783:         {
784:             // Convert common truthy/falsy values
785:             condition = condition.Trim().ToLowerInvariant();
786:             
787:             if (bool.TryParse(condition, out bool result))
788:             {
789:                 return result;
790:             }
791:             
792:             if (int.TryParse(condition, out int intResult))
793:             {
794:                 return intResult != 0;
795:             }
796:             
797:             // Consider truthy string values
798:             return condition == "true" || condition == "yes" || condition == "success" || condition == "y" || condition == "1";
799:         }
800:         
801:         /// <summary>
802:         /// Substitutes parameter references in a run condition with their values.
803:         /// </summary>
804:         /// <param name="condition">The condition string.</param>
805:         /// <param name="stepResults">Results from previous steps.</param>
806:         /// <returns>The substituted condition.</returns>
807:         private string SubstituteStepReferences(string condition, Dictionary<string, CustomToolExecutionResult> stepResults)
808:         {
809:             // We support references like {step1.exit-code}, {step1.output}, etc.
810:             // Use a simple string replacement for now
811:             string result = condition;
812:             
813:             foreach (var stepEntry in stepResults)
814:             {
815:                 var stepName = stepEntry.Key;
816:                 var stepResult = stepEntry.Value;
817:                 
818:                 result = result
819:                     .Replace($"{{{stepName}.exit-code}}", stepResult.ExitCode.ToString())
820:                     .Replace($"{{{stepName}.output}}", stepResult.Output)
821:                     .Replace($"{{{stepName}.error}}", stepResult.Error);
