827:         /// <summary>
828:         /// Substitutes parameter references in a command with their values.
829:         /// </summary>
830:         /// <param name="command">The command string.</param>
831:         /// <param name="parameters">The parameters to substitute.</param>
832:         /// <returns>The command with substituted parameters.</returns>
833:         private string SubstituteParameters(string command, Dictionary<string, object?> parameters)
834:         {
835:             // Simple string replacement for now
836:             string result = command;
837:             
838:             foreach (var param in parameters)
839:             {
840:                 string value = GetParameterValueString(param.Value);
841:                 
842:                 // Check if we need to escape shell metacharacters
843:                 // In a real implementation, we'd look up the parameter definition
844:                 // and check if it has security.escape-shell = true
845:                 bool shouldEscape = param.Key.EndsWith("_SECURE");
846:                 
847:                 if (shouldEscape)
848:                 {
849:                     value = EscapeShellMetacharacters(value);
850:                 }
851:                 
852:                 result = result.Replace($"{{{param.Key}}}", value);
853:             }
854:             
855:             return result;
856:         }
