858:         /// <summary>
859:         /// Converts a parameter value to a string representation.
860:         /// </summary>
861:         /// <param name="value">The parameter value.</param>
862:         /// <returns>String representation of the value.</returns>
863:         private string GetParameterValueString(object? value)
864:         {
865:             if (value == null)
866:             {
867:                 return string.Empty;
868:             }
869:             
870:             // Handle arrays
871:             if (value is System.Collections.IEnumerable enumerable && !(value is string))
872:             {
873:                 var items = new List<string>();
874:                 foreach (var item in enumerable)
875:                 {
876:                     items.Add(item?.ToString() ?? string.Empty);
877:                 }
878:                 
879:                 return string.Join(" ", items);
880:             }
881:             
882:             return value.ToString() ?? string.Empty;
883:         }
