885:         /// <summary>
886:         /// Escapes shell metacharacters in a string.
887:         /// </summary>
888:         /// <param name="value">The value to escape.</param>
889:         /// <returns>The escaped value.</returns>
890:         private string EscapeShellMetacharacters(string value)
891:         {
892:             // Escape common shell metacharacters
893:             var metacharacters = new[] { '\\', '"', '\'', ' ', '&', '|', ';', '<', '>', '(', ')', '$', '`', '\n', '\r', '\t' };
894:             
895:             var result = new StringBuilder();
896:             foreach (char c in value)
897:             {
898:                 if (metacharacters.Contains(c))
899:                 {
900:                     result.Append('\\');
901:                 }
902:                 result.Append(c);
903:             }
904:             
905:             return result.ToString();
906:         }
