908:         /// <summary>
909:         /// Substitutes parameter references in a command with their values.
910:         /// </summary>
911:         /// <param name="command">The command string.</param>
912:         /// <param name="parameters">The parameters to substitute.</param>
913:         /// <param name="stepResults">Results from previous steps.</param>
914:         /// <returns>The command with substituted parameters.</returns>
915:         private string SubstituteParameters(
916:             string command,
917:             Dictionary<string, object?> parameters,
918:             Dictionary<string, CustomToolExecutionResult> stepResults)
919:         {
920:             // First substitute regular parameters
921:             string result = SubstituteParameters(command, parameters);
922:             
923:             // Then substitute step references
924:             foreach (var stepEntry in stepResults)
925:             {
926:                 var stepName = stepEntry.Key;
927:                 var stepResult = stepEntry.Value;
928:                 
929:                 // Escape output and error if they contain sensitive content
930:                 string output = stepResult.Output;
931:                 string error = stepResult.Error;
932:                 
933:                 // Replace step references
934:                 result = result
935:                     .Replace($"{{{stepName}.exit-code}}", stepResult.ExitCode.ToString())
936:                     .Replace($"{{{stepName}.output}}", output)
937:                     .Replace($"{{{stepName}.error}}", error);
938:             }
939:             
940:             return result;
941:         }
