943:         /// <summary>
944:         /// Normalizes a file path based on the operating system and tool configuration.
945:         /// </summary>
946:         /// <param name="path">The path to normalize.</param>
947:         /// <param name="tool">The tool definition with path handling configuration.</param>
948:         /// <returns>The normalized path.</returns>
949:         private string NormalizePath(string path, CustomToolDefinition tool)
950:         {
951:             if (string.IsNullOrEmpty(path))
952:             {
953:                 return path;
954:             }
955:             
956:             // Check if path normalization is enabled
957:             if (true)
958:             {
959:                 // Apply working directory if the path is relative
960:                 if (!Path.IsPathRooted(path) && !string.IsNullOrEmpty(tool.WorkingDirectory))
961:                 {
962:                     // Substitute any parameters in the working directory
963:                     var workingDir = tool.WorkingDirectory;
964:                     path = Path.Combine(workingDir, path);
965:                 }
966:                 
967:                 // Resolve temp directory references
968:                 if (path.Contains("{TEMP_DIRECTORY}"))
969:                 {
970:                     string tempDir = Path.GetTempPath();
971:                     path = path.Replace("{TEMP_DIRECTORY}", tempDir);
972:                 }
973:                 
974:                 // Cross-platform path handling
975:                 if (true)
976:                 {
977:                     // Convert path separators based on the current platform
978:                     if (RuntimeInformation.IsOSPlatform(OSPlatform.Windows))
979:                     {
980:                         // Convert to Windows path format
981:                         path = path.Replace("/", "\\");
982:                     }
983:                     else
984:                     {
985:                         // Convert to Unix path format
986:                         path = path.Replace("\\", "/");
987:                     }
988:                 }
989:                 
990:                 // Get canonical form of the path
991:                 try
992:                 {
993:                     // Use GetFullPath to normalize path separators and resolve relative segments
994:                     path = Path.GetFullPath(path);
995:                 }
996:                 catch (Exception ex)
997:                 {
998:                     ConsoleHelpers.WriteDebugLine($"Error normalizing path '{path}': {ex.Message}");
999:                 }
1000:             }
1001:             
1002:             return path;
1003:         }
1004:         
1005:         /// <summary>
1006:         /// Normalizes a working directory path.
1007:         /// </summary>
1008:         /// <param name="workingDirectory">The working directory to normalize.</param>
1009:         /// <returns>The normalized working directory.</returns>
1010:         private string NormalizeWorkingDirectory(string workingDirectory)
1011:         {
1012:             if (string.IsNullOrEmpty(workingDirectory))
1013:             {
1014:                 return Directory.GetCurrentDirectory();
1015:             }
1016:             
1017:             // Expand environment variables
1018:             workingDirectory = Environment.ExpandEnvironmentVariables(workingDirectory);
1019:             
1020:             // Handle home directory references (~)
1021:             if (workingDirectory.StartsWith("~/") || workingDirectory.StartsWith("~\\"))
1022:             {
1023:                 string homeDir = Environment.GetFolderPath(Environment.SpecialFolder.UserProfile);
1024:                 workingDirectory = Path.Combine(homeDir, workingDirectory.Substring(2));
1025:             }
1026:             
1027:             // Ensure the directory exists
1028:             if (!Directory.Exists(workingDirectory))
1029:             {
1030:                 try
1031:                 {
1032:                     Directory.CreateDirectory(workingDirectory);
1033:                 }
1034:                 catch (Exception ex)
1035:                 {
1036:                     ConsoleHelpers.WriteDebugLine($"Error creating working directory '{workingDirectory}': {ex.Message}");
1037:                     return Directory.GetCurrentDirectory();
1038:                 }
1039:             }
1040:             
1041:             return Path.GetFullPath(workingDirectory);
1042:         }
