1058:         /// <summary>
1059:         /// Runs the tests for a custom tool.
1060:         /// </summary>
1061:         /// <param name="tool">The tool definition.</param>
1062:         /// <returns>The test results.</returns>
1063:         public async Task<CustomToolTestResults> RunToolTestsAsync(CustomToolDefinition tool)
1064:         {
1065:             var testResults = new CustomToolTestResults
1066:             {
1067:                 ToolName = tool.Name,
1068:                 TestResults = new List<CustomToolTestResult>()
1069:             };
1070:             
1071:             if (tool.Tests == null || tool.Tests.Count == 0)
1072:             {
1073:                 ConsoleHelpers.WriteDebugLine($"No tests defined for tool '{tool.Name}'");
1074:                 return testResults;
1075:             }
1076:             
1077:             foreach (var test in tool.Tests)
1078:             {
1079:                 ConsoleHelpers.WriteDebugLine($"Running test '{test.Name}' for tool '{tool.Name}'");
1080:                 
1081:                 // Set up parameters
1082:                 var parameters = new Dictionary<string, object?>();
1083:                 
1084:                 // Add parameters from the test
1085:                 if (test.Parameters != null)
1086:                 {
1087:                     foreach (var param in test.Parameters)
1088:                     {
1089:                         parameters[param.Key] = param.Value;
1090:                     }
1091:                 }
1092:                 
1093:                 // Execute the tool with test parameters
1094:                 var result = await ExecuteToolAsync(tool, parameters);
1095:                 
1096:                 // Evaluate test expectations
1097:                 bool passed = true;
1098:                 var failureReasons = new List<string>();
1099:                 
1100:                 if (test.Expected != null)
1101:                 {
1102:                     // Check exit code
1103:                     if (test.Expected.ExitCode.HasValue && 
1104:                         result.ExitCode != test.Expected.ExitCode.Value)
1105:                     {
1106:                         passed = false;
1107:                         failureReasons.Add($"Exit code mismatch: expected {test.Expected.ExitCode.Value}, got {result.ExitCode}");
1108:                     }
1109:                     
1110:                     // Check output contains
1111:                     if (!string.IsNullOrEmpty(test.Expected.OutputContains) && 
1112:                         !result.Output.Contains(test.Expected.OutputContains))
1113:                     {
1114:                         passed = false;
1115:                         failureReasons.Add($"Output does not contain expected text: '{test.Expected.OutputContains}'");
1116:                     }
1117:                     
1118:                     // Check file exists
1119:                     if (!string.IsNullOrEmpty(test.Expected.FileExists))
1120:                     {
1121:                         string filePath = NormalizePath(test.Expected.FileExists, tool);
1122:                         if (!File.Exists(filePath))
1123:                         {
1124:                             passed = false;
1125:                             failureReasons.Add($"Expected file does not exist: '{filePath}'");
1126:                         }
1127:                     }
1128:                     
1129:                     // Check directory exists
1130:                     if (!string.IsNullOrEmpty(test.Expected.DirectoryExists))
1131:                     {
1132:                         string dirPath = NormalizePath(test.Expected.DirectoryExists, tool);
1133:                         if (!Directory.Exists(dirPath))
1134:                         {
1135:                             passed = false;
1136:                             failureReasons.Add($"Expected directory does not exist: '{dirPath}'");
1137:                         }
1138:                     }
1139:                 }
1140:                 
1141:                 // Run cleanup commands if specified
1142:                 if (test.Cleanup != null && test.Cleanup.Count > 0)
1143:                 {
1144:                     foreach (var cleanupCommand in test.Cleanup)
1145:                     {
1146:                         try
1147:                         {
1148:                             await ExecuteCommandAsync(cleanupCommand, ShellType.Bash, tool);
1149:                         }
1150:                         catch (Exception ex)
1151:                         {
1152:                             ConsoleHelpers.WriteDebugLine($"Error during test cleanup: {ex.Message}");
1153:                         }
1154:                     }
1155:                 }
1156:                 
1157:                 // Add test result
1158:                 testResults.TestResults.Add(new CustomToolTestResult
1159:                 {
1160:                     TestName = test.Name,
1161:                     Passed = passed,
1162:                     FailureReasons = failureReasons,
1163:                     Output = result.Output,
1164:                     Error = result.Error,
1165:                     ExitCode = result.ExitCode
1166:                 });
1167:                 
1168:                 ConsoleHelpers.WriteDebugLine($"Test '{test.Name}' {(passed ? "passed" : "failed")}");
1169:             }
1170:             
1171:             return testResults;
1172:         }
1173:     }
