31:             if (tool.Type == "alias")
32:             {
33:                 return await ExecuteAliasToolAsync(tool, parameters);
34:             }
35: 
36:             if (tool.Steps != null && tool.Steps.Count > 0)
37:             {
38:                 return await ExecuteMultiStepToolAsync(tool, parameters);
39:             }
40:             
41:             return await ExecuteSingleStepToolAsync(tool, parameters);
42:         }
