87:         [YamlMember(Alias = "timeout")]
88:         public int Timeout { get; set; } = 60000;
89: 
90:         /// <summary>
91:         /// Gets or sets the working directory.
92:         /// </summary>
93:         [YamlMember(Alias = "working-directory")]
94:         public string? WorkingDirectory { get; set; }
95: 
96:         /// <summary>
97:         /// Gets or sets the platforms supported by this tool.
98:         /// </summary>
99:         [YamlMember(Alias = "platforms")]
100:         public List<string> Platforms { get; set; } = new();
101: 
102:         /// <summary>
103:         /// Gets or sets the tags for categorization and security.
104:         /// </summary>
105:         [YamlMember(Alias = "tags")]
106:         public List<string> Tags { get; set; } = new();
107: 
108:         /// <summary>
109:         /// Gets or sets whether to ignore errors.
110:         /// </summary>
111:         [YamlMember(Alias = "ignore-errors")]
112:         public bool IgnoreErrors { get; set; } = false;
113: 
114:         /// <summary>
115:         /// Gets or sets the data to pass via stdin.
116:         /// </summary>
117:         [YamlMember(Alias = "input")]
118:         public string? Input { get; set; }
119: 
120:         /// <summary>
121:         /// Gets or sets environment variables for the tool.
122:         /// </summary>
123:         [YamlMember(Alias = "environment")]
124:         public CustomToolEnvironment? Environment { get; set; }
125: 
126:         /// <summary>
127:         /// Gets or sets function calling schema configuration.
128:         /// </summary>
129:         [YamlMember(Alias = "function-calling")]
130:         public CustomToolFunctionCalling? FunctionCalling { get; set; }
131: 
132:         /// <summary>
133:         /// Gets or sets security configuration for the tool.
134:         /// </summary>
135:         [YamlMember(Alias = "security")]
136:         public CustomToolSecurity? Security { get; set; }
137: 
138:         /// <summary>
139:         /// Gets or sets file path configuration for the tool.
140:         /// </summary>
141:         [YamlMember(Alias = "file-paths")]
142:         public CustomToolFilePaths? FilePaths { get; set; }
143: 
144:         /// <summary>
145:         /// Gets or sets metadata for categorization and discovery.
146:         /// </summary>
147:         [YamlMember(Alias = "metadata")]
148:         public CustomToolMetadata? Metadata { get; set; }
149: 
150:         /// <summary>
151:         /// Gets or sets tests for the tool.
152:         /// </summary>
153:         [YamlMember(Alias = "tests")]
154:         public List<CustomToolTest>? Tests { get; set; }
155: 
156:         /// <summary>
157:         /// Gets or sets resource constraints for the tool.
158:         /// </summary>
159:         [YamlMember(Alias = "resources")]
160:         public CustomToolResources? Resources { get; set; }
161: 
162:         /// <summary>
163:         /// Gets or sets whether this tool is interactive.
164:         /// </summary>
165:         [YamlMember(Alias = "interactive")]
166:         public bool Interactive { get; set; } = false;
167: 
168:         /// <summary>
169:         /// Gets or sets interactive options for the tool.
170:         /// </summary>
171:         [YamlMember(Alias = "interactive-options")]
172:         public CustomToolInteractiveOptions? InteractiveOptions { get; set; }
173: 
174:         /// <summary>
175:         /// Gets or sets whether this tool is an alias.
176:         /// </summary>
177:         [YamlMember(Alias = "type")]
178:         public string? Type { get; set; }
179: 
180:         /// <summary>
181:         /// Gets or sets the base tool for an alias.
182:         /// </summary>
183:         [YamlMember(Alias = "base-tool")]
184:         public string? BaseTool { get; set; }
185: 
186:         /// <summary>
187:         /// Gets or sets default parameters for an alias.
188:         /// </summary>
189:         [YamlMember(Alias = "default-parameters")]
190:         public Dictionary<string, string>? DefaultParameters { get; set; }
191: 
192:         /// <summary>
193:         /// Gets or sets the changelog for the tool.
194:         /// </summary>
195:         [YamlMember(Alias = "changelog")]
196:         public List<CustomToolChangelogEntry>? Changelog { get; set; }
197: 
198:         /// <summary>
199:         /// Validates the tool definition.
200:         /// </summary>
201:         /// <param name="errorMessage">The error message if validation fails.</param>
202:         /// <returns>True if valid, false otherwise.</returns>
203:         public bool Validate(out string? errorMessage)
204:         {
205:             errorMessage = null;
206: 
207:             // Validate required fields
208:             if (string.IsNullOrEmpty(Name))
209:             {
210:                 errorMessage = "Tool name is required.";
211:                 return false;
212:             }
213: 
214:             if (string.IsNullOrEmpty(Description))
215:             {
216:                 errorMessage = "Tool description is required.";
217:                 return false;
218:             }
219: 
220:             // Validate that at least one command type is specified
221:             bool hasCommand = !string.IsNullOrEmpty(BashCommand) ||
222:                               !string.IsNullOrEmpty(CmdCommand) ||
223:                               !string.IsNullOrEmpty(PowerShellCommand) ||
224:                               !string.IsNullOrEmpty(RunCommand) ||
225:                               !string.IsNullOrEmpty(Script) ||
226:                               Steps.Count > 0;
227: 
228:             if (!hasCommand)
229:             {
230:                 errorMessage = "Tool must have at least one command or steps.";
231:                 return false;
232:             }
233: 
234:             // If script is specified, shell must also be specified
235:             if (!string.IsNullOrEmpty(Script) && string.IsNullOrEmpty(Shell))
236:             {
237:                 errorMessage = "Shell must be specified when using a script.";
238:                 return false;
239:             }
240: 
241:             // Validate steps
242:             foreach (var step in Steps)
243:             {
244:                 if (string.IsNullOrEmpty(step.Name))
245:                 {
246:                     errorMessage = "All steps must have a name.";
247:                     return false;
248:                 }
249: 
250:                 bool hasStepCommand = !string.IsNullOrEmpty(step.BashCommand) ||
251:                                      !string.IsNullOrEmpty(step.CmdCommand) ||
252:                                      !string.IsNullOrEmpty(step.PowerShellCommand) ||
253:                                      !string.IsNullOrEmpty(step.RunCommand) ||
254:                                      !string.IsNullOrEmpty(step.UseTool);
255: 
256:                 if (!hasStepCommand)
257:                 {
258:                     errorMessage = $"Step '{step.Name}' must have a command.";
259:                     return false;
260:                 }
261: 
262:                 // If using another tool, validate that the with parameters match
263:                 if (!string.IsNullOrEmpty(step.UseTool) && (step.With == null || step.With.Count == 0))
264:                 {
265:                     // This is just a warning, not an error
266:                     Console.WriteLine($"Warning: Step '{step.Name}' uses tool '{step.UseTool}' but doesn't provide any parameters.");
267:                 }
268:             }
269: 
270:             // Check for duplicate step names
271:             var stepNames = new HashSet<string>();
272:             foreach (var step in Steps)
273:             {
274:                 if (!stepNames.Add(step.Name))
275:                 {
276:                     errorMessage = $"Duplicate step name: '{step.Name}'.";
277:                     return false;
278:                 }
279:             }
280: 
281:             // Set parameter names and validate parameters
282:             foreach (var param in Parameters)
283:             {
284:                 param.Value.Name = param.Key;
285: 
286:                 if (string.IsNullOrEmpty(param.Value.Description))
287:                 {
288:                     errorMessage = $"Parameter '{param.Key}' must have a description.";
289:                     return false;
290:                 }
291: 
292:                 // Validate parameter type
293:                 string paramType = param.Value.Type.ToLowerInvariant();
294:                 if (paramType != "string" && paramType != "number" && paramType != "boolean" && paramType != "array" && paramType != "object")
295:                 {
296:                     errorMessage = $"Parameter '{param.Key}' has invalid type: '{param.Value.Type}'. Must be one of: string, number, boolean, array, object.";
297:                     return false;
298:                 }
299: 
300:                 // Validate default value against parameter type if provided
301:                 if (param.Value.Default != null)
302:                 {
303:                     if (!param.Value.Validate(param.Value.Default, out string? paramError))
304:                     {
305:                         errorMessage = paramError;
306:                         return false;
307:                     }
308:                 }
309:             }
310: 
311:             // Validate parameter references in commands
312:             HashSet<string> missingParams = new HashSet<string>();
313: 
314:             // Check parameters in main commands
315:             CheckParameterReferences(BashCommand, missingParams);
316:             CheckParameterReferences(CmdCommand, missingParams);
317:             CheckParameterReferences(PowerShellCommand, missingParams);
318:             CheckParameterReferences(RunCommand, missingParams);
319:             CheckParameterReferences(Script, missingParams);
320: 
321:             // Check parameters in step commands
322:             foreach (var step in Steps)
323:             {
324:                 CheckParameterReferences(step.BashCommand, missingParams);
325:                 CheckParameterReferences(step.CmdCommand, missingParams);
326:                 CheckParameterReferences(step.PowerShellCommand, missingParams);
327:                 CheckParameterReferences(step.RunCommand, missingParams);
328:             }
329: 
330:             // Check for missing parameters
331:             if (missingParams.Count > 0)
332:             {
333:                 errorMessage = $"The following parameters are referenced but not defined: {string.Join(", ", missingParams)}";
334:                 return false;
335:             }
336: 
337:             return true;
338:         }
339: 
340:         private void CheckParameterReferences(string? command, HashSet<string> missingParams)
341:         {
342:             if (string.IsNullOrEmpty(command))
343:             {
344:                 return;
345:             }
346: 
347:             // Find parameter references like {PARAM_NAME}
348:             var matches = System.Text.RegularExpressions.Regex.Matches(command, @"\{([^}]+)\}");
349:             foreach (System.Text.RegularExpressions.Match match in matches)
350:             {
351:                 string paramName = match.Groups[1].Value;
352: 
353:                 // Skip step references
354:                 if (paramName.Contains("."))
355:                 {
356:                     continue;
357:                 }
358: 
359:                 // Skip parameter exists check for environment variables and other special parameters
360:                 if (paramName.StartsWith("ENV_") || 
361:                     paramName == "WORKSPACE" || 
362:                     paramName == "TOOL_NAME" || 
363:                     paramName == "INPUT_PARAM")
364:                 {
365:                     continue;
366:                 }
367: 
368:                 // Check if parameter is defined
369:                 if (!Parameters.ContainsKey(paramName))
370:                 {
371:                     missingParams.Add(paramName);
372:                 }
373:             }
374:         }
375:     }
376: 
377:     /// <summary>
378:     /// Represents a parameter for a custom tool.
379:     /// </summary>
380:     public class CustomToolParameter
381:     {
382:         /// <summary>
383:         /// Gets or sets the type of the parameter.
384:         /// </summary>
385:         [YamlMember(Alias = "type")]
386:         public string Type { get; set; } = "string";
387: 
388:         /// <summary>
389:         /// Gets or sets the description of the parameter.
390:         /// </summary>
391:         [YamlMember(Alias = "description")]
392:         public string Description { get; set; } = string.Empty;
393: 
394:         /// <summary>
395:         /// Gets or sets whether the parameter is required.
396:         /// </summary>
397:         [YamlMember(Alias = "required")]
398:         public bool Required { get; set; } = false;
399: 
400:         /// <summary>
401:         /// Gets or sets the default value of the parameter.
402:         /// </summary>
403:         [YamlMember(Alias = "default")]
404:         public object? Default { get; set; }
405: 
406:         /// <summary>
407:         /// Gets or sets validation rules for the parameter.
408:         /// </summary>
409:         [YamlMember(Alias = "validation")]
410:         public Dictionary<string, object>? Validation { get; set; }
411: 
412:         /// <summary>
413:         /// Gets or sets the transform function to apply to the parameter.
414:         /// </summary>
415:         [YamlMember(Alias = "transform")]
416:         public string? Transform { get; set; }
417: 
418:         /// <summary>
419:         /// Gets or sets the format string for the parameter.
420:         /// </summary>
421:         [YamlMember(Alias = "format")]
422:         public string? Format { get; set; }
423: 
424:         /// <summary>
425:         /// Gets or sets examples for the parameter.
426:         /// </summary>
427:         [YamlMember(Alias = "examples")]
428:         public List<string>? Examples { get; set; }
429: 
430:         /// <summary>
431:         /// Gets or sets detailed help text for the parameter.
432:         /// </summary>
433:         [YamlMember(Alias = "detailed-help")]
434:         public string? DetailedHelp { get; set; }
435: 
436:         /// <summary>
437:         /// Gets or sets security options for the parameter.
438:         /// </summary>
439:         [YamlMember(Alias = "security")]
440:         public CustomToolParameterSecurity? Security { get; set; }
441: 
442:         /// <summary>
443:         /// Gets or sets the name of the parameter.
444:         /// </summary>
445:         [YamlIgnore]
446:         public string Name { get; set; } = string.Empty;
447: 
448:         /// <summary>
449:         /// Validates a parameter value against the parameter definition.
450:         /// </summary>
451:         /// <param name="value">The value to validate.</param>
452:         /// <param name="errorMessage">The error message if validation fails.</param>
453:         /// <returns>True if validation passes, false otherwise.</returns>
454:         public bool Validate(object? value, out string? errorMessage)
455:         {
456:             errorMessage = null;
457: 
458:             // Check if required
459:             if (Required && value == null)
460:             {
461:                 errorMessage = $"Parameter '{Name}' is required.";
462:                 return false;
463:             }
464: 
465:             // If null and not required, validation passes
466:             if (value == null)
467:             {
468:                 return true;
469:             }
470: 
471:             // Validate by type
472:             switch (Type?.ToLowerInvariant())
473:             {
474:                 case "string":
475:                     return ValidateString(value.ToString(), out errorMessage);
476:                 case "number":
477:                     return ValidateNumber(value, out errorMessage);
478:                 case "boolean":
479:                     return ValidateBoolean(value, out errorMessage);
480:                 case "array":
481:                     return ValidateArray(value, out errorMessage);
482:                 case "object":
483:                     return ValidateObject(value, out errorMessage);
484:                 default:
485:                     // Default to string validation if type is not specified
486:                     return ValidateString(value.ToString(), out errorMessage);
487:             }
488:         }
489: 
490:         private bool ValidateString(string? value, out string? errorMessage)
491:         {
492:             errorMessage = null;
493: 
494:             if (value == null)
495:             {
496:                 return true;
497:             }
498: 
499:             if (Validation != null)
500:             {
501:                 // Validate string length
502:                 if (Validation.ContainsKey("minLength"))
503:                 {
504:                     if (int.TryParse(Validation["minLength"].ToString(), out int minLength))
505:                     {
506:                         if (value.Length < minLength)
507:                         {
508:                             errorMessage = $"Parameter '{Name}' must be at least {minLength} characters long.";
509:                             return false;
510:                         }
511:                     }
512:                 }
513: 
514:                 if (Validation.ContainsKey("maxLength"))
515:                 {
516:                     if (int.TryParse(Validation["maxLength"].ToString(), out int maxLength))
517:                     {
518:                         if (value.Length > maxLength)
519:                         {
520:                             errorMessage = $"Parameter '{Name}' must be no more than {maxLength} characters long.";
521:                             return false;
522:                         }
523:                     }
524:                 }
525: 
526:                 // Validate string pattern
527:                 if (Validation.ContainsKey("pattern"))
528:                 {
529:                     string pattern = Validation["pattern"].ToString();
530:                     try
531:                     {
532:                         if (!System.Text.RegularExpressions.Regex.IsMatch(value, pattern))
533:                         {
534:                             errorMessage = $"Parameter '{Name}' does not match the required pattern: {pattern}.";
535:                             return false;
536:                         }
537:                     }
538:                     catch (Exception ex)
539:                     {
540:                         errorMessage = $"Invalid pattern for parameter '{Name}': {ex.Message}";
541:                         return false;
542:                     }
543:                 }
544: 
545:                 // Validate enum values
546:                 if (Validation.ContainsKey("enum"))
547:                 {
548:                     if (Validation["enum"] is List<object> enumValues)
549:                     {
550:                         bool found = false;
551:                         foreach (var enumValue in enumValues)
552:                         {
553:                             if (value.Equals(enumValue.ToString(), StringComparison.OrdinalIgnoreCase))
554:                             {
555:                                 found = true;
556:                                 break;
557:                             }
558:                         }
559: 
560:                         if (!found)
561:                         {
562:                             errorMessage = $"Parameter '{Name}' must be one of: {string.Join(", ", enumValues)}.";
563:                             return false;
564:                         }
565:                     }
566:                 }
567:             }
568: 
569:             return true;
570:         }
571: 
572:         private bool ValidateNumber(object value, out string? errorMessage)
573:         {
574:             errorMessage = null;
575: 
576:             // Try to convert to decimal for validation
577:             if (!decimal.TryParse(value.ToString(), out decimal numericValue))
578:             {
579:                 errorMessage = $"Parameter '{Name}' must be a valid number.";
580:                 return false;
581:             }
582: 
583:             if (Validation != null)
584:             {
585:                 // Validate minimum value
586:                 if (Validation.ContainsKey("minimum"))
587:                 {
588:                     if (decimal.TryParse(Validation["minimum"].ToString(), out decimal minimum))
589:                     {
590:                         if (numericValue < minimum)
591:                         {
592:                             errorMessage = $"Parameter '{Name}' must be at least {minimum}.";
593:                             return false;
594:                         }
595:                     }
596:                 }
597: 
598:                 // Validate maximum value
599:                 if (Validation.ContainsKey("maximum"))
600:                 {
601:                     if (decimal.TryParse(Validation["maximum"].ToString(), out decimal maximum))
602:                     {
603:                         if (numericValue > maximum)
604:                         {
605:                             errorMessage = $"Parameter '{Name}' must be no more than {maximum}.";
606:                             return false;
607:                         }
608:                     }
609:                 }
610: 
611:                 // Validate multiple of
612:                 if (Validation.ContainsKey("multipleOf"))
613:                 {
614:                     if (decimal.TryParse(Validation["multipleOf"].ToString(), out decimal multipleOf))
615:                     {
616:                         if (multipleOf != 0 && numericValue % multipleOf != 0)
617:                         {
618:                             errorMessage = $"Parameter '{Name}' must be a multiple of {multipleOf}.";
619:                             return false;
620:                         }
621:                     }
622:                 }
623:             }
624: 
625:             return true;
626:         }
627: 
628:         private bool ValidateBoolean(object value, out string? errorMessage)
629:         {
630:             errorMessage = null;
631: 
632:             // Try to convert to boolean for validation
633:             string strValue = value.ToString().ToLowerInvariant();
634:             if (strValue != "true" && strValue != "false" && 
635:                 strValue != "1" && strValue != "0" &&
636:                 strValue != "yes" && strValue != "no")
637:             {
638:                 errorMessage = $"Parameter '{Name}' must be a valid boolean (true/false, 1/0, yes/no).";
639:                 return false;
640:             }
641: 
642:             return true;
643:         }
644: 
645:         private bool ValidateArray(object value, out string? errorMessage)
646:         {
647:             errorMessage = null;
648: 
649:             // Check if the value is actually an array
650:             if (!(value is System.Collections.IEnumerable enumerable) || value is string)
651:             {
652:                 errorMessage = $"Parameter '{Name}' must be an array.";
653:                 return false;
654:             }
655: 
656:             if (Validation != null)
657:             {
658:                 // Count items for validation
659:                 int count = 0;
660:                 foreach (var _ in enumerable)
661:                 {
662:                     count++;
663:                 }
664: 
665:                 // Validate minimum items
666:                 if (Validation.ContainsKey("minItems"))
667:                 {
668:                     if (int.TryParse(Validation["minItems"].ToString(), out int minItems))
669:                     {
670:                         if (count < minItems)
671:                         {
672:                             errorMessage = $"Parameter '{Name}' must have at least {minItems} items.";
673:                             return false;
674:                         }
675:                     }
676:                 }
677: 
678:                 // Validate maximum items
679:                 if (Validation.ContainsKey("maxItems"))
680:                 {
681:                     if (int.TryParse(Validation["maxItems"].ToString(), out int maxItems))
682:                     {
683:                         if (count > maxItems)
684:                         {
685:                             errorMessage = $"Parameter '{Name}' must have no more than {maxItems} items.";
686:                             return false;
687:                         }
688:                     }
689:                 }
690: 
691:                 // Validate unique items
692:                 if (Validation.ContainsKey("uniqueItems"))
693:                 {
694:                     if (bool.TryParse(Validation["uniqueItems"].ToString(), out bool uniqueItems) && uniqueItems)
695:                     {
696:                         var items = new HashSet<string>();
697:                         foreach (var item in enumerable)
698:                         {
699:                             string itemStr = item?.ToString() ?? string.Empty;
700:                             if (!items.Add(itemStr))
701:                             {
702:                                 errorMessage = $"Parameter '{Name}' must have unique items.";
703:                                 return false;
704:                             }
705:                         }
706:                     }
707:                 }
708:             }
709: 
710:             return true;
711:         }
712: 
713:         private bool ValidateObject(object value, out string? errorMessage)
714:         {
715:             errorMessage = null;
716: 
717:             // For now, basic validation that it's a dictionary or similar
718:             if (!(value is System.Collections.IDictionary))
719:             {
720:                 errorMessage = $"Parameter '{Name}' must be an object.";
721:                 return false;
722:             }
723: 
724:             // Additional object validation could be added here
725:             return true;
