728:         /// <summary>
729:         /// Transforms a parameter value according to the transformation rules.
730:         /// </summary>
731:         /// <param name="value">The value to transform.</param>
732:         /// <returns>The transformed value.</returns>
733:         public object? TransformValue(object? value)
734:         {
735:             if (value == null || string.IsNullOrEmpty(Transform))
736:             {
737:                 return value;
738:             }
739: 
740:             string strValue = value.ToString();
741: 
742:             // Apply transformation based on the transform expression
743:             switch (Transform.ToLowerInvariant())
744:             {
745:                 case "lowercase":
746:                 case "tolowercase":
747:                 case "tolower":
748:                     return strValue.ToLowerInvariant();
749: 
750:                 case "uppercase":
751:                 case "touppercase":
752:                 case "toupper":
753:                     return strValue.ToUpperInvariant();
754: 
755:                 case "trim":
756:                     return strValue.Trim();
757: 
758:                 case "int":
759:                 case "integer":
760:                 case "floor":
761:                 case "math.floor":
762:                     if (decimal.TryParse(strValue, out decimal decValue))
763:                     {
764:                         return Math.Floor(decValue);
765:                     }
766:                     return value;
767: 
768:                 case "ceil":
769:                 case "ceiling":
770:                 case "math.ceiling":
771:                 case "math.ceil":
772:                     if (decimal.TryParse(strValue, out decimal ceilValue))
773:                     {
774:                         return Math.Ceiling(ceilValue);
775:                     }
776:                     return value;
777: 
778:                 case "round":
779:                 case "math.round":
780:                     if (decimal.TryParse(strValue, out decimal roundValue))
781:                     {
782:                         return Math.Round(roundValue);
783:                     }
784:                     return value;
785: 
786:                 case "boolean":
787:                 case "bool":
788:                     string loweredValue = strValue.ToLowerInvariant();
789:                     return loweredValue == "true" || loweredValue == "1" || loweredValue == "yes";
790: 
791:                 default:
792:                     // If it's a custom transformation, return as is for now
793:                     // A more sophisticated transformation system could be implemented later
794:                     return value;
795:             }
796:         }
797:     }
