6: namespace CycoDev.CustomTools
7: {
8:     /// <summary>
9:     /// Factory for creating AITool functions from custom tools.
10:     /// </summary>
11:     public class CustomToolFunctionFactory : FunctionFactory
12:     {
13:         private readonly CustomToolFactory _toolFactory;
14:         private readonly CustomToolExecutor _toolExecutor;
15:         private readonly Dictionary<string, CustomToolDefinition> _registeredTools = new();
16: 
17:         /// <summary>
18:         /// Initializes a new instance of the <see cref="CustomToolFunctionFactory"/> class.
19:         /// </summary>
20:         /// <param name="toolFactory">The custom tool factory.</param>
21:         public CustomToolFunctionFactory(CustomToolFactory toolFactory)
22:         {
23:             _toolFactory = toolFactory;
24:             _toolExecutor = new CustomToolExecutor();
25:         }
26: 
27:         /// <summary>
28:         /// Loads all custom tools and creates functions for them.
29:         /// </summary>
30:         /// <returns>A task representing the asynchronous operation.</returns>
31:         public async Task LoadCustomToolsAsync()
32:         {
33:             await _toolFactory.LoadAllToolsAsync();
34:             RegisterCustomToolFunctions();
35:         }
36: 
37:         /// <summary>
38:         /// Loads custom tools from a specific scope and creates functions for them.
39:         /// </summary>
40:         /// <param name="scope">The scope to load tools from.</param>
41:         /// <returns>A task representing the asynchronous operation.</returns>
42:         public async Task LoadCustomToolsFromScopeAsync(ToolScope scope)
43:         {
44:             await _toolFactory.LoadToolsFromScopeAsync(scope);
45:             RegisterCustomToolFunctions();
46:         }
47: 
48:         /// <summary>
49:         /// Registers all custom tools as functions.
50:         /// </summary>
51:         private void RegisterCustomToolFunctions()
52:         {
53:             foreach (var tool in _toolFactory.GetAllTools())
54:             {
55:                 RegisterCustomToolFunction(tool);
56:             }
57:         }
58: 
59:         /// <summary>
60:         /// Registers a custom tool as a function.
61:         /// </summary>
62:         /// <param name="tool">The custom tool to register.</param>
63:         private void RegisterCustomToolFunction(CustomToolDefinition tool)
64:         {
65:             // Skip if this tool is already registered
66:             if (_registeredTools.ContainsKey(tool.Name))
67:             {
68:                 return;
69:             }
70: 
71:             // Create AIFunction
72:             var aiFunction = AIFunctionFactory.Create(
73:                 method: GetType().GetMethod(nameof(ExecuteCustomTool), System.Reflection.BindingFlags.Instance | System.Reflection.BindingFlags.NonPublic)!,
74:                 instance: this,
75:                 name: tool.Name,
76:                 description: tool.Description);
77: 
78:             // Add function to factory
79:             AddFunction(aiFunction);
80:             _registeredTools[tool.Name] = tool;
81: 
82:             ConsoleHelpers.WriteDebugLine($"Registered custom tool '{tool.Name}' as function");
83:         }
84: 
85:         /// <summary>
86:         /// Executes a custom tool.
87:         /// </summary>
88:         /// <param name="functionName">The name of the function/tool to execute.</param>
89:         /// <param name="args">The arguments for the function call as a JSON string.</param>
90:         /// <returns>The result of the tool execution.</returns>
91:         [EditorBrowsable(EditorBrowsableState.Never)]
92:         private async Task<string> ExecuteCustomTool(string functionName, string args)
93:         {
94:             if (!_registeredTools.TryGetValue(functionName, out var tool))
95:             {
96:                 return $"Error: Custom tool '{functionName}' not found";
97:             }
98: 
99:             try
100:             {
101:                 var parameters = JsonSerializer.Deserialize<Dictionary<string, object?>>(args, new JsonSerializerOptions { PropertyNameCaseInsensitive = true });
102:                 parameters ??= new Dictionary<string, object?>();
103: 
104:                 var result = await _toolExecutor.ExecuteToolAsync(tool, parameters);
105:                 return result.IsError
106:                     ? $"Error: {result.Error}\n{result.Output}"
107:                     : result.Output;
108:             }
109:             catch (Exception ex)
110:             {
111:                 return $"Error executing custom tool '{functionName}': {ex.Message}";
112:             }
113:         }
114: 
115:         /// <summary>
116:         /// Checks if a function corresponds to a custom tool.
117:         /// </summary>
118:         /// <param name="functionName">The name of the function.</param>
119:         /// <returns>True if the function is a custom tool, false otherwise.</returns>
120:         public bool IsCustomTool(string functionName)
121:         {
122:             return _registeredTools.ContainsKey(functionName);
123:         }
124: 
125:         /// <summary>
126:         /// Executes a custom tool.
127:         /// </summary>
128:         /// <param name="functionName">The name of the function/tool to execute.</param>
129:         /// <param name="functionArguments">The arguments for the function call as a JSON string.</param>
130:         /// <param name="result">The result of the function call, if successful.</param>
131:         /// <returns>True if the function was found and called, false otherwise.</returns>
132:         public override bool TryCallFunction(string functionName, string functionArguments, out object? result)
133:         {
134:             result = null;
135: 
136:             if (!string.IsNullOrEmpty(functionName) && IsCustomTool(functionName))
137:             {
138:                 var task = ExecuteCustomTool(functionName, functionArguments);
139:                 task.Wait();
140:                 result = task.Result;
141:                 return true;
142:             }
143: 
144:             return base.TryCallFunction(functionName, functionArguments, out result);
145:         }
146:     }
