4: namespace CycoDev.CustomTools
5: {
6:     /// <summary>
7:     /// Provides utility methods for handling paths across different platforms.
8:     /// </summary>
9:     public static class PathUtils
10:     {
11:         /// <summary>
12:         /// Gets whether the current platform is Windows.
13:         /// </summary>
14:         public static bool IsWindows => RuntimeInformation.IsOSPlatform(OSPlatform.Windows);
15: 
16:         /// <summary>
17:         /// Gets whether the current platform is macOS.
18:         /// </summary>
19:         public static bool IsMacOS => RuntimeInformation.IsOSPlatform(OSPlatform.OSX);
20: 
21:         /// <summary>
22:         /// Gets whether the current platform is Linux.
23:         /// </summary>
24:         public static bool IsLinux => RuntimeInformation.IsOSPlatform(OSPlatform.Linux);
25: 
26:         /// <summary>
27:         /// Gets the platform-specific directory separator.
28:         /// </summary>
29:         public static char DirectorySeparator => Path.DirectorySeparatorChar;
30: 
31:         /// <summary>
32:         /// Gets the platform-specific path separator.
33:         /// </summary>
34:         public static char PathSeparator => Path.PathSeparator;
35: 
36:         /// <summary>
37:         /// Normalizes a path to the current platform's format.
38:         /// </summary>
39:         /// <param name="path">The path to normalize.</param>
40:         /// <returns>The normalized path.</returns>
41:         public static string NormalizePath(string path)
42:         {
43:             if (string.IsNullOrEmpty(path))
44:             {
45:                 return path;
46:             }
47: 
48:             // Replace all separators with the platform-specific separator
49:             return path.Replace('\\', DirectorySeparator).Replace('/', DirectorySeparator);
50:         }
51: 
52:         /// <summary>
53:         /// Converts a path to Windows format.
54:         /// </summary>
55:         /// <param name="path">The path to convert.</param>
56:         /// <returns>The Windows-formatted path.</returns>
57:         public static string ToWindowsPath(string path)
58:         {
59:             if (string.IsNullOrEmpty(path))
60:             {
61:                 return path;
62:             }
63: 
64:             return path.Replace('/', '\\');
65:         }
66: 
67:         /// <summary>
68:         /// Converts a path to Unix format.
69:         /// </summary>
70:         /// <param name="path">The path to convert.</param>
71:         /// <returns>The Unix-formatted path.</returns>
72:         public static string ToUnixPath(string path)
73:         {
74:             if (string.IsNullOrEmpty(path))
75:             {
76:                 return path;
77:             }
78: 
79:             return path.Replace('\\', '/');
80:         }
81: 
82:         /// <summary>
83:         /// Resolves a path to its absolute form.
84:         /// </summary>
85:         /// <param name="path">The path to resolve.</param>
86:         /// <param name="workingDirectory">The working directory to resolve relative paths from.</param>
87:         /// <returns>The absolute path.</returns>
88:         public static string ResolvePath(string path, string workingDirectory)
89:         {
90:             if (string.IsNullOrEmpty(path))
91:             {
92:                 return path;
93:             }
94: 
95:             // If the path is already absolute, return it
96:             if (Path.IsPathRooted(path))
97:             {
98:                 return path;
99:             }
100: 
101:             // Otherwise, resolve it relative to the working directory
102:             return Path.GetFullPath(Path.Combine(workingDirectory, path));
103:         }
104: 
105:         /// <summary>
106:         /// Replaces placeholder variables in a path with their values.
107:         /// </summary>
108:         /// <param name="path">The path containing placeholders.</param>
109:         /// <param name="variables">A dictionary of placeholder variables and their values.</param>
110:         /// <returns>The path with placeholders replaced.</returns>
111:         public static string ReplacePlaceholders(string path, Dictionary<string, string> variables)
112:         {
113:             if (string.IsNullOrEmpty(path))
114:             {
115:                 return path;
116:             }
117: 
118:             foreach (var variable in variables)
119:             {
120:                 path = path.Replace($"{{{variable.Key}}}", variable.Value);
121:             }
122: 
123:             return path;
124:         }
125: 
126:         /// <summary>
127:         /// Gets the platform-specific temporary directory.
128:         /// </summary>
129:         /// <returns>The temporary directory path.</returns>
130:         public static string GetTempDirectory()
131:         {
132:             return Path.GetTempPath();
133:         }
134: 
135:         /// <summary>
136:         /// Gets a platform-specific temporary directory for a tool.
137:         /// </summary>
138:         /// <param name="toolName">The name of the tool.</param>
139:         /// <returns>The tool-specific temporary directory path.</returns>
140:         public static string GetToolTempDirectory(string toolName)
141:         {
142:             string tempDir = Path.Combine(GetTempDirectory(), "cycod-tools", toolName);
143:             Directory.CreateDirectory(tempDir);
144:             return tempDir;
145:         }
146: 
147:         /// <summary>
148:         /// Sanitizes a path to be used in a shell command.
149:         /// </summary>
150:         /// <param name="path">The path to sanitize.</param>
151:         /// <returns>The sanitized path.</returns>
152:         public static string SanitizePathForShell(string path)
153:         {
154:             if (string.IsNullOrEmpty(path))
155:             {
156:                 return path;
157:             }
158: 
159:             // For Windows, wrap the path in quotes if it contains spaces
160:             if (IsWindows)
161:             {
162:                 if (path.Contains(" ") && !path.StartsWith("\"") && !path.EndsWith("\""))
163:                 {
164:                     return $"\"{path}\"";
165:                 }
166:             }
167:             // For Unix-like systems, escape spaces
168:             else
169:             {
170:                 if (path.Contains(" "))
171:                 {
172:                     return path.Replace(" ", "\\ ");
173:                 }
174:             }
175: 
176:             return path;
177:         }
178:     }
179: }

