  318:         var slashResult = await _slashCommandRouter.HandleAsync(userPrompt, chat);
  319:         if (slashResult.Handled)
  320:         {
  321:             skipAssistant = slashResult.SkipAssistant;
  322:             giveAssistant = slashResult.ResponseText;
  323:             
  324:             // Handle immediate save if requested
  325:             if (slashResult.NeedsSave)
  326:             {
  327:                 TrySaveChatHistoryToFile(AutoSaveOutputChatHistory);
  328:                 if (OutputChatHistory != AutoSaveOutputChatHistory)
  329:                 {
  330:                     TrySaveChatHistoryToFile(OutputChatHistory);
  331:                 }
  332:                 
  333:                 // Update trajectory metadata to match conversation state after title changes
  334:                 SetTrajectoryMetadata(_currentChat?.Conversation.Metadata);
  335:                 
  336:                 ConsoleHelpers.WriteDebugLine("Slash command triggered immediate save");
  337:             }
  338:             
  339:             return (skipAssistant, giveAssistant);
  340:         }
  341: 
  342:         // Handle built-in commands that don't need router
  343:         if (userPrompt.StartsWith("/save"))
  344:         {
  345:             skipAssistant = HandleSaveChatHistoryCommand(chat, userPrompt.Substring("/save".Length).Trim());
  346:         }
  347:         else if (userPrompt == "/clear")
  348:         {
  349:             skipAssistant = HandleClearChatHistoryCommand(chat);
  350:         }
  351:         else if (userPrompt == "/cost")
  352:         {
  353:             skipAssistant = HandleShowCostCommand();
  354:         }
  355:         else if (userPrompt == "/help")
  356:         {
  357:             skipAssistant = HandleHelpCommand();
  358:         }
  359: 
  360:         return (skipAssistant, giveAssistant);
  361:     }
