  449:     private void AppendUserDefinedPromptsToHelp(StringBuilder helpBuilder)
  450:     {
  451:         // Get all scopes for prompt files
  452:         bool foundPrompts = false;
  453:         foreach (var scope in new[] { ConfigFileScope.Local, ConfigFileScope.User, ConfigFileScope.Global })
  454:         {
  455:             var promptDir = PromptFileHelpers.FindPromptDirectoryInScope(scope);
  456:             if (promptDir == null || !Directory.Exists(promptDir)) continue;
  457:             
  458:             var promptFiles = Directory.GetFiles(promptDir, "*.prompt")
  459:                 .OrderBy(file => Path.GetFileNameWithoutExtension(file))
  460:                 .ToList();
  461:                 
  462:             if (promptFiles.Count == 0) continue;
  463:             
  464:             foundPrompts = true;
  465:             
  466:             // Format location header consistently
  467:             var locationDisplay = CommonDisplayHelpers.FormatLocationHeader(promptDir, scope);
  468:             helpBuilder.AppendLine($"  LOCATION: {locationDisplay}");
  469:             helpBuilder.AppendLine();
  470: 
  471:             var indent = new string(' ', 4);
  472:             
  473:             foreach (var promptFile in promptFiles)
  474:             {
  475:                 var promptName = Path.GetFileNameWithoutExtension(promptFile);
  476:                 helpBuilder.AppendLine($"{indent}/{promptName}");
  477:                 helpBuilder.AppendLine();
  478: 
  479:                 // Get prompt content and truncate it for preview
  480:                 var promptText = PromptFileHelpers.GetPromptText(promptName, scope)!;
  481:                 var truncatedContent = CommonDisplayHelpers.TruncateContent(
  482:                     promptText, 
  483:                     CommonDisplayHelpers.DefaultMaxContentLines, 
  484:                     CommonDisplayHelpers.DefaultMaxContentWidth, 
  485:                     8);  // deeper indentation for content
  486:                 
  487:                 helpBuilder.AppendLine(truncatedContent);
  488:                 helpBuilder.AppendLine();
  489:             }
  490:             
  491:             helpBuilder.AppendLine();
  492:         }
  493:         
  494:         if (!foundPrompts)
  495:         {
  496:             helpBuilder.AppendLine("  No custom prompts found.");
  497:             helpBuilder.AppendLine();
  498:         }
  499:     }
