  639:     /// <summary>
  640:     /// Attempts to generate and save a title for the conversation using cycodmd.
  641:     /// </summary>
  642:     private async Task TryGenerateAndSaveTitle(string? filePath)
  643:     {
  644:         if (filePath == null || _currentChat?.Conversation.Metadata == null) 
  645:         {
  646:             ConsoleHelpers.WriteDebugLine($"Skipping title generation: filePath={filePath}, metadata={_currentChat?.Conversation.Metadata != null}");
  647:             return;
  648:         }
  649:         
  650:         ConsoleHelpers.WriteDebugLine($"Starting title generation for: {filePath}");
  651:         
  652:         // Mark title generation as in progress
  653:         if (!_currentChat.Notifications.TryStartGeneration(NotificationType.Title))
  654:         {
  655:             ConsoleHelpers.WriteDebugLine("Title generation already in progress, skipping background generation");
  656:             return;
  657:         }
  658:         
  659:         try
  660:         {
  661:             var generatedTitle = await TitleGenerationHelpers.GenerateTitleAsync(filePath);
  662:             if (!string.IsNullOrEmpty(generatedTitle))
  663:             {
  664:                 ConsoleHelpers.WriteDebugLine($"Generated title: '{generatedTitle}', setting to metadata");
  665:                 
  666:                 // Store current title as old title for revert functionality
  667:                 _currentChat.Notifications.SetOldTitle(_currentChat.Conversation.Metadata?.Title);
  668: 
  669:                 _currentChat.Conversation.SetGeneratedTitle(generatedTitle);
  670:                 
  671:                 // Save again with updated title
  672:                 ConsoleHelpers.WriteDebugLine($"Saving conversation with updated title to: {filePath}");
  673:                 _currentChat.Conversation.SaveToFile(filePath, useOpenAIFormat: ChatHistoryDefaults.UseOpenAIFormat);
  674:                 
  675:                 // Update trajectory metadata with new title
  676:                 SetTrajectoryMetadata(_currentChat.Conversation.Metadata);
  677:                 
  678:                 // Update console title with new auto-generated title
  679:                 ConsoleTitleHelper.UpdateWindowTitle(_currentChat.Conversation.Metadata);
  680:                 
  681:                 // Complete generation successfully with notification
  682:                 _currentChat.Notifications.CompleteGeneration(NotificationType.Title, generatedTitle, NotificationFormat.Success);
  683:                 
  684:                 ConsoleHelpers.WriteDebugLine($"Successfully generated and saved title: '{generatedTitle}'");
  685:             }
  686:             else
  687:             {
  688:                 ConsoleHelpers.WriteDebugLine("Title generation returned empty result");
  689:             }
  690:         }
  691:         catch (Exception ex)
  692:         {
  693:             // Handle failure with user notification and debug logging
  694:             var userMessage = "Title generation failed - please try /title refresh to retry";
  695:             _currentChat.Notifications.FailGeneration(NotificationType.Title, userMessage);
  696:             ConsoleHelpers.WriteDebugLine($"Background title generation failed: {ex.Message}");
  697:         }
  698:         // No finally block needed - CompleteGeneration/FailGeneration handle state cleanup internally
  699:     }
