  747:     private bool HandleFunctionCallApproval(McpFunctionFactory factory, string name, string args)
  748:     {
  749:         Logger.Info($"HandleFunctionCallApproval: Function '{name}' called with args: {args}");
  750:         
  751:         var autoApprove = ShouldAutoApprove(factory, name);
  752:         Logger.Info($"HandleFunctionCallApproval: Auto-approve result for '{name}': {autoApprove}");
  753:         
  754:         if (autoApprove) return true;
  755: 
  756:         while (true)
  757:         {
  758:             var approvePrompt = " Approve? (Y/n or ?) ";
  759:             var erasePrompt = new string(' ', approvePrompt.Length);
  760:             EnsureLineFeeds();
  761:             ConsoleHelpers.DisplayAssistantFunctionCall(name, args, null);
  762:             ConsoleHelpers.Write(approvePrompt, ConsoleColor.Yellow);
  763: 
  764:             var shouldDeny = ShouldDenyFunctionCall(factory, name);
  765:             Logger.Info($"HandleFunctionCallApproval: Should deny '{name}': {shouldDeny}");
  766:             
  767:             ConsoleKeyInfo? key = shouldDeny ? null : ConsoleHelpers.ReadKey(true);
  768:             ConsoleHelpers.DisplayAssistantFunctionCall(name, args, null);
  769:             ConsoleHelpers.Write(erasePrompt, ColorHelpers.MapColor(ConsoleColor.DarkBlue));
  770:             ConsoleHelpers.DisplayAssistantFunctionCall(name, args, null);
  771: 
  772:             if (key?.KeyChar == 'Y' || key?.Key == ConsoleKey.Enter)
  773:             {
  774:                 ConsoleHelpers.WriteLine($"\b\b\b\b Approved (session)", ConsoleColor.Yellow);
  775:                 _approvedFunctionCallNames.Add(name);
  776:                 return true;
  777:             }
  778:             else if (key == null || key?.KeyChar == 'N')
  779:             {
  780:                 _deniedFunctionCallNames.Add(name);
  781:                 ConsoleHelpers.WriteLine($"\b\b\b\b Declined (session)", ConsoleColor.Red);
  782:                 return false;
  783:             }
  784:             else if (key?.KeyChar == 'y')
  785:             {
  786:                 ConsoleHelpers.WriteLine($"\b\b\b\b Approved (once)", ConsoleColor.Yellow);
  787:                 return true;
  788:             }
  789:             else if (key?.KeyChar == 'n')
  790:             {
  791:                 ConsoleHelpers.WriteLine($"\b\b\b\b Declined (once)", ConsoleColor.Red);
  792:                 return false;
  793:             }
  794:             else if (key?.KeyChar == '?')
  795:             {
  796:                 ConsoleHelpers.WriteLine($"\b\b\b\b Help\n", ConsoleColor.Yellow);
  797:                 ConsoleHelpers.WriteLine("  Enter: Approve this function call for this session");
  798:                 ConsoleHelpers.WriteLine("  Y: Approve this function call for this session");
  799:                 ConsoleHelpers.WriteLine("  y: Approve this function call for this one time");
  800:                 ConsoleHelpers.WriteLine("  N: Decline this function call for this session");
  801:                 ConsoleHelpers.WriteLine("  n: Decline this function call for this one time");
  802:                 ConsoleHelpers.WriteLine("  ?: Show this help message\n");
  803:                 ConsoleHelpers.Write("  See ");
  804:                 ConsoleHelpers.Write("cycod help function calls", ConsoleColor.Yellow);
  805:                 ConsoleHelpers.WriteLine(" for more information.\n");
  806:             }
  807:             else
  808:             {
  809:                 ConsoleHelpers.WriteLine($"\b\b\b\b Invalid input", ConsoleColor.Red);
  810:             }
  811:         }
  812:     }
