  606:                         }
  607:                     }
  608:                     
  609:                     if (!string.IsNullOrEmpty(fullName) && !seenRepos.Contains(fullName))
  610:                     {
  611:                         repoNames.Add(fullName);
  612:                         seenRepos.Add(fullName);
