  671:     private static RepoInfo? ParseRepoInfo(JsonElement repoElement)
  672:     {
  673:         try
  674:         {
  675:             var url = repoElement.GetProperty("url").GetString();
  676:             
  677:             // Try to get name - might be "name" (repo search) or part of "nameWithOwner" (code search)
  678:             string? name = null;
  679:             string? owner = null;
  680:             
  681:             if (repoElement.TryGetProperty("name", out var nameElement))
  682:             {
  683:                 name = nameElement.GetString();
  684:             }
  685:             else if (repoElement.TryGetProperty("nameWithOwner", out var nameWithOwnerElement))
  686:             {
  687:                 var fullName = nameWithOwnerElement.GetString();
  688:                 if (!string.IsNullOrEmpty(fullName))
  689:                 {
  690:                     var parts = fullName.Split('/');
  691:                     if (parts.Length == 2)
  692:                     {
  693:                         owner = parts[0];
  694:                         name = parts[1];
  695:                     }
  696:                 }
  697:             }
  698:             
  699:             if (string.IsNullOrEmpty(url) || string.IsNullOrEmpty(name))
  700:             {
  701:                 return null;
  702:             }
  703: 
  704:             // Try to get owner if we didn't get it from nameWithOwner
  705:             if (string.IsNullOrEmpty(owner))
  706:             {
  707:                 owner = repoElement.TryGetProperty("owner", out var ownerElement) && 
  708:                            ownerElement.TryGetProperty("login", out var loginElement)
  709:                     ? loginElement.GetString() ?? "unknown"
  710:                     : "unknown";
  711:             }
  712: 
  713:             var stars = repoElement.TryGetProperty("stargazersCount", out var starsElement)
  714:                 ? starsElement.GetInt32()
  715:                 : 0;
  716: 
  717:             var language = repoElement.TryGetProperty("language", out var langElement) && langElement.ValueKind != JsonValueKind.Null
  718:                 ? langElement.GetString()
  719:                 : null;
  720: 
  721:             var description = repoElement.TryGetProperty("description", out var descElement) && descElement.ValueKind != JsonValueKind.Null
  722:                 ? descElement.GetString()
  723:                 : null;
  724: 
  725:             DateTime? updatedAt = null;
  726:             if (repoElement.TryGetProperty("updatedAt", out var updatedElement))
  727:             {
  728:                 var updatedStr = updatedElement.GetString();
  729:                 if (!string.IsNullOrEmpty(updatedStr) && DateTime.TryParse(updatedStr, out var parsed))
  730:                 {
  731:                     updatedAt = parsed;
  732:                 }
  733:             }
  734: 
  735:             var forks = repoElement.TryGetProperty("forksCount", out var forksElement)
  736:                 ? forksElement.GetInt32()
  737:                 : 0;
  738: 
  739:             var openIssues = repoElement.TryGetProperty("openIssuesCount", out var issuesElement)
  740:                 ? issuesElement.GetInt32()
  741:                 : 0;
  742: 
  743:             var isFork = repoElement.TryGetProperty("isFork", out var forkElement) && forkElement.GetBoolean();
  744: 
  745:             return new RepoInfo
  746:             {
  747:                 Url = url,
  748:                 Name = name,
  749:                 Owner = owner,
  750:                 Stars = stars,
  751:                 Language = language,
  752:                 Description = description,
  753:                 UpdatedAt = updatedAt,
  754:                 Forks = forks,
  755:                 OpenIssues = openIssues,
  756:                 IsFork = isFork,
  757:                 Topics = null  // GraphQL query doesn't include topics by default
  758:             };
  759:         }
  760:         catch
  761:         {
  762:             return null;
  763:         }
  764:     }
