  82:                 
  83:                 var preFilteredRepos = await CycoGr.Helpers.GitHubSearchHelpers.SearchCodeForRepositoriesAsync(
  84:                     command.RepoFileContains,
  85:                     command.Language,
  86:                     command.Owner,
  87:                     command.MinStars,
  88:                     command.RepoFileContainsExtension,  // Pass extension for filtering
  89:                     command.MaxResults * 2); // Get more repos initially since we'll filter further
  90:                 
  91:                 if (preFilteredRepos.Count == 0)
  92:                 {
  93:                     ConsoleHelpers.WriteLine("No repositories found matching the pre-filter criteria", ConsoleColor.Yellow, overrideQuiet: true);
