  96:                 
  97:                 ConsoleHelpers.WriteLine($"Found {preFilteredRepos.Count} repositories matching pre-filter", ConsoleColor.Green, overrideQuiet: true);
  98:                 ConsoleHelpers.WriteLine(overrideQuiet: true);
