  103:             
  104:             // Stage 2: Phase E - Dual behavior for --file-contains
  105:             // If --file-contains is specified WITHOUT repo pre-filtering, use it to find repos first
  106:             var hasRepoPreFiltering = !string.IsNullOrEmpty(command.RepoFileContains) || command.Repos.Any() || command.RepoPatterns.Any();
  107:             var hasFileContains = !string.IsNullOrEmpty(command.FileContains);
