  903:         return ext switch
  904:         {
  905:             ".cs" => "csharp",
  906:             ".js" => "javascript",
  907:             ".ts" => "typescript",
  908:             ".py" => "python",
  909:             ".java" => "java",
  910:             ".cpp" or ".cc" or ".cxx" => "cpp",
  911:             ".c" => "c",
  912:             ".h" or ".hpp" => "cpp",
  913:             ".go" => "go",
  914:             ".rs" => "rust",
  915:             ".rb" => "ruby",
  916:             ".php" => "php",
  917:             ".swift" => "swift",
  918:             ".kt" or ".kts" => "kotlin",
  919:             ".scala" => "scala",
  920:             ".sh" or ".bash" => "bash",
  921:             ".ps1" => "powershell",
  922:             ".sql" => "sql",
  923:             ".html" or ".htm" => "html",
  924:             ".css" => "css",
  925:             ".xml" => "xml",
  926:             ".json" => "json",
  927:             ".yaml" or ".yml" => "yaml",
  928:             ".md" or ".markdown" => "markdown",
