766:     public static async Task<List<string>> CloneRepositoriesAsync(
767:         List<RepoInfo> repos,
768:         bool asSubmodules,
769:         string cloneDirectory,
770:         int maxClone)
771:     {
772:         var clonedRepos = new List<string>();
773:         var actualMaxClone = Math.Min(maxClone, repos.Count);
774:         
775:         // Create clone directory if it doesn't exist
776:         if (!Directory.Exists(cloneDirectory))
777:         {
778:             Directory.CreateDirectory(cloneDirectory);
779:             Logger.Info($"Created directory: {cloneDirectory}");
780:         }
781:         
782:         for (int i = 0; i < actualMaxClone; i++)
783:         {
784:             var repo = repos[i];
785:             var repoName = repo.Name;
786:             var targetPath = Path.Combine(cloneDirectory, repoName);
787:             
788:             // Skip if already exists
789:             if (Directory.Exists(targetPath))
790:             {
791:                 ConsoleHelpers.WriteLine($"Skipping {repoName} (already exists)", ConsoleColor.Yellow);
792:                 Logger.Warning($"Repository already exists: {targetPath}");
793:                 clonedRepos.Add(targetPath);
794:                 continue;
795:             }
796:             
797:             try
798:             {
799:                 ConsoleHelpers.DisplayStatus($"Cloning {repoName} ({i + 1}/{actualMaxClone})...");
800:                 
801:                 if (asSubmodules)
802:                 {
803:                     await CloneAsSubmoduleAsync(repo.Url, targetPath);
804:                 }
805:                 else
806:                 {
807:                     await CloneRepositoryAsync(repo.Url, targetPath);
808:                 }
809:                 
810:                 clonedRepos.Add(targetPath);
811:                 ConsoleHelpers.WriteLine($"Cloned: {repoName}", ConsoleColor.Green);
812:             }
813:             catch (Exception ex)
814:             {
815:                 ConsoleHelpers.WriteErrorLine($"Failed to clone {repoName}: {ex.Message}");
816:                 Logger.Error($"Error cloning repository {repo.Url}: {ex.Message}");
817:             }
818:         }
819:         
820:         ConsoleHelpers.DisplayStatusErase();
821:         return clonedRepos;
822:     }
