824:     private static async Task CloneRepositoryAsync(string url, string targetPath)
825:     {
826:         var gitCommand = $"git clone {url} \"{targetPath}\"";
827:         var result = await ProcessHelpers.RunProcessAsync(gitCommand, workingDirectory: null, envVars: null, input: null, timeout: null);
828:         
829:         if (result.ExitCode != 0)
830:         {
831:             var errorMsg = !string.IsNullOrEmpty(result.StandardError) 
832:                 ? result.StandardError 
833:                 : "Unknown error executing git clone";
834:             throw new Exception($"Git clone failed: {errorMsg}");
835:         }
836:     }
