  641:     private static async Task FormatAndOutputCodeResults(List<CycoGr.Models.CodeMatch> codeMatches, int contextLines, string query, string format, List<Tuple<string, string>> fileInstructionsList, List<string> repoInstructionsList, List<string> instructionsList, CycoGr.CommandLineCommands.SearchCommand command, bool overrideQuiet = false)
  642:     {
  643:         // Group by repository
  644:         var byRepo = codeMatches.GroupBy(m => m.Repository.FullName).ToList();
  645: 
  646:         var allRepoOutputs = new List<string>();
  647: 
  648:         foreach (var repoGroup in byRepo)
  649:         {
  650:             var repo = repoGroup.First().Repository;
  651:             var files = repoGroup.ToList();
  652: 
  653:             var repoOutputBuilder = new System.Text.StringBuilder();
  654: 
  655:             // Output repo header
  656:             var header = $"## {repo.FullName}";
  657:             if (repo.Stars > 0)
  658:             {
  659:                 header += $" (⭐ {repo.FormattedStars})";
  660:             }
  661:             if (!string.IsNullOrEmpty(repo.Language))
  662:             {
  663:                 header += $" ({repo.Language})";
  664:             }
  665:             repoOutputBuilder.AppendLine(header);
  666:             repoOutputBuilder.AppendLine();
  667: 
  668:             // Repo URL and Desc
  669:             repoOutputBuilder.AppendLine($"Repo: {repo.Url}");
  670:             if (!string.IsNullOrEmpty(repo.Description))
  671:             {
  672:                 repoOutputBuilder.AppendLine($"Desc: {repo.Description}");
  673:             }
  674:             repoOutputBuilder.AppendLine();
  675: 
  676:             // Count total matches across all files
  677:             var totalMatches = files.Sum(f => f.TextMatches?.Count ?? 0);
  678:             var fileCount = files.Select(f => f.Path).Distinct().Count();
  679:             
  680:             repoOutputBuilder.AppendLine($"Found {fileCount} file(s) with {totalMatches} matches:");
  681:             
  682:             // Output file URLs with match counts
  683:             var fileGroups = files.GroupBy(f => f.Path).ToList();
  684:             foreach (var fileGroup in fileGroups)
  685:             {
  686:                 var firstMatch = fileGroup.First();
  687:                 var matchCount = fileGroup.Sum(f => f.TextMatches?.Count ?? 0);
  688:                 repoOutputBuilder.AppendLine($"- {firstMatch.Url} ({matchCount} matches)");
  689:             }
  690:             repoOutputBuilder.AppendLine();
  691: 
  692:             // Process files in parallel using ThrottledProcessor
  693:             var throttledProcessor = new ThrottledProcessor(Environment.ProcessorCount);
  694:             var fileOutputs = await throttledProcessor.ProcessAsync(
  695:                 fileGroups,
  696:                 async fileGroup => await ProcessFileGroupAsync(fileGroup, repo, query, contextLines, fileInstructionsList, command, overrideQuiet)
  697:             );
  698: 
  699:             // Combine all file outputs for this repo
  700:             var repoFilesOutput = string.Join("", fileOutputs);
  701:             repoOutputBuilder.Append(repoFilesOutput);
  702: 
  703:             // Get the complete repo output
  704:             var repoOutput = repoOutputBuilder.ToString();
  705: 
  706:             // Apply repo instructions if any
  707:             if (repoInstructionsList.Any())
  708:             {
  709:                 Logger.Info($"Applying {repoInstructionsList.Count} repo instruction(s) to repository: {repo.FullName}");
  710:                 repoOutput = AiInstructionProcessor.ApplyAllInstructions(
  711:                     repoInstructionsList,
  712:                     repoOutput,
  713:                     useBuiltInFunctions: false,
  714:                     saveChatHistory: string.Empty);
  715:                 Logger.Info($"Repo instructions applied successfully to repository: {repo.FullName}");
  716:             }
  717: 
  718:             // Collect this repo's output
  719:             allRepoOutputs.Add(repoOutput);
  720:         }
  721: 
  722:         // Combine all repo outputs
  723:         var combinedOutput = string.Join("\n", allRepoOutputs);
  724: 
  725:         // Apply final/global instructions if any
  726:         if (instructionsList.Any())
  727:         {
  728:             Logger.Info($"Applying {instructionsList.Count} final instruction(s) to all combined output");
  729:             combinedOutput = AiInstructionProcessor.ApplyAllInstructions(
  730:                 instructionsList,
  731:                 combinedOutput,
  732:                 useBuiltInFunctions: false,
  733:                 saveChatHistory: string.Empty);
  734:             Logger.Info($"Final instructions applied successfully");
  735:         }
  736: 
  737:         // Output the final result
  738:         ConsoleHelpers.WriteLine(combinedOutput, overrideQuiet: overrideQuiet);
  739:     }
