  741:     private static async Task<string> ProcessFileGroupAsync(
  742:         IGrouping<string, CycoGr.Models.CodeMatch> fileGroup, 
  743:         CycoGr.Models.RepoInfo repo,
  744:         string query, 
  745:         int contextLines,
  746:         List<Tuple<string, string>> fileInstructionsList,
  747:         CycoGr.CommandLineCommands.SearchCommand command,
  748:         bool overrideQuiet)
  749:     {
  750:         var firstMatch = fileGroup.First();
  751:         var output = new System.Text.StringBuilder();
  752:         
  753:         // File header
  754:         output.AppendLine($"## {firstMatch.Path}");
  755:         output.AppendLine();
  756: 
  757:         // Fetch full file content and display with real line numbers
  758:         var rawUrl = ConvertToRawUrl(firstMatch.Url);
  759:         try
  760:         {
  761:             // Create FoundTextFile with lambda to load content
  762:             var foundFile = new FoundTextFile
  763:             {
  764:                 Path = firstMatch.Path,
  765:                 LoadContent = async () =>
  766:                 {
  767:                     using var httpClient = new System.Net.Http.HttpClient();
  768:                     httpClient.DefaultRequestHeaders.UserAgent.ParseAdd("CycoGr/1.0");
  769:                     return await httpClient.GetStringAsync(rawUrl);
  770:                 },
  771:                 Metadata = new Dictionary<string, object>
  772:                 {
  773:                     { "Repository", repo },
  774:                     { "Sha", firstMatch.Sha },
  775:                     { "Url", firstMatch.Url }
  776:                 }
  777:             };
  778: 
  779:             // Load the content
  780:             foundFile.Content = await foundFile.LoadContent();
  781: 
  782:             // Use LineHelpers to filter and display with real line numbers
  783:             // Determine which patterns to use for line filtering
  784:             List<System.Text.RegularExpressions.Regex> includePatterns;
  785:             
  786:             if (command.LineContainsPatterns.Any())
  787:             {
  788:                 // Use explicit --line-contains patterns if specified
  789:                 includePatterns = command.LineContainsPatterns
  790:                     .Select(p => new System.Text.RegularExpressions.Regex(p, System.Text.RegularExpressions.RegexOptions.IgnoreCase))
  791:                     .ToList();
  792:             }
  793:             else
  794:             {
  795:                 // Fallback to using the search query
  796:                 includePatterns = new List<System.Text.RegularExpressions.Regex>
  797:                 {
  798:                     new System.Text.RegularExpressions.Regex(System.Text.RegularExpressions.Regex.Escape(query), System.Text.RegularExpressions.RegexOptions.IgnoreCase)
  799:                 };
  800:             }
  801:             
  802:             var excludePatterns = new List<System.Text.RegularExpressions.Regex>();
  803: 
  804:             var lang = DetectLanguageFromPath(firstMatch.Path);
  805:             var backticks = $"```{lang}";
  806:             
  807:             var filteredContent = LineHelpers.FilterAndExpandContext(
  808:                 foundFile.Content,
  809:                 includePatterns,
  810:                 contextLines,  // lines before
  811:                 contextLines,  // lines after
  812:                 true,          // include line numbers
  813:                 excludePatterns,
  814:                 backticks,
  815:                 true           // highlight matches
  816:             );
  817: 
  818:             if (filteredContent != null)
  819:             {
  820:                 output.AppendLine(backticks);
  821:                 output.AppendLine(filteredContent);
  822:                 output.AppendLine("```");
  823:             }
  824:             else
  825:             {
  826:                 output.AppendLine("(No matches found in full file content)");
  827:             }
  828:         }
  829:         catch (Exception ex)
  830:         {
  831:             output.AppendLine($"Error fetching file content: {ex.Message}");
  832:             output.AppendLine("Falling back to fragment display...");
  833:             
  834:             // Fallback to fragment display
  835:             foreach (var match in fileGroup)
  836:             {
  837:                 if (match.TextMatches?.Any() == true)
  838:                 {
  839:                     var lang = DetectLanguageFromPath(match.Path);
  840:                     output.AppendLine($"```{lang}");
  841:                     
  842:                     foreach (var textMatch in match.TextMatches)
  843:                     {
  844:                         var fragment = textMatch.Fragment;
  845:                         output.AppendLine(fragment);
  846:                     }
  847:                     
  848:                     output.AppendLine("```");
  849:                 }
  850:             }
  851:         }
  852: 
  853:         output.AppendLine();
  854:         output.AppendLine($"Raw: {rawUrl}");
  855:         output.AppendLine();
  856: 
  857:         // Apply file instructions if any match this file
  858:         var formattedOutput = output.ToString();
  859:         var instructionsForThisFile = fileInstructionsList
  860:             .Where(x => FileNameMatchesInstructionsCriteria(firstMatch.Path, x.Item2))
  861:             .Select(x => x.Item1)
  862:             .ToList();
  863: 
  864:         if (instructionsForThisFile.Any())
  865:         {
  866:             Logger.Info($"Applying {instructionsForThisFile.Count} instruction(s) to file: {firstMatch.Path}");
  867:             formattedOutput = AiInstructionProcessor.ApplyAllInstructions(
  868:                 instructionsForThisFile, 
  869:                 formattedOutput, 
  870:                 useBuiltInFunctions: false, 
  871:                 saveChatHistory: string.Empty);
  872:             Logger.Info($"Instructions applied successfully to file: {firstMatch.Path}");
  873:         }
  874: 
  875:         return formattedOutput;
  876:     }
