  948:     private static string FormatCodeAsDetailed(List<CycoGr.Models.CodeMatch> matches, int contextLines, string query)
  949:     {
  950:         var output = new StringBuilder();
  951:         var repoGroups = matches.GroupBy(m => m.Repository.Url).OrderBy(g => g.Key);
  952: 
  953:         foreach (var repoGroup in repoGroups)
  954:         {
  955:             var firstMatch = repoGroup.First();
  956:             var repo = firstMatch.Repository;
  957:             var lang = repo.Language ?? "unknown";
  958:             
  959:             // Repo header: # owner/repo (⭐ stars | language)
  960:             output.AppendLine($"# {repo.FullName} (⭐ {repo.FormattedStars} | {lang})");
  961:             output.AppendLine();
  962: 
  963:             foreach (var match in repoGroup.OrderBy(m => m.Path))
  964:             {
  965:                 // File header: ## path/to/file
  966:                 output.AppendLine($"## {match.Path}");
  967:                 output.AppendLine();
  968: 
  969:                 // Get language for code fence from file extension
  970:                 var fileExt = Path.GetExtension(match.Path).TrimStart('.');
  971:                 var fenceLanguage = GetLanguageForExtension(fileExt);
  972: 
  973:                 // Code fence with matches
  974:                 output.AppendLine($"```{fenceLanguage}");
  975: 
  976:                 foreach (var textMatch in match.TextMatches)
  977:                 {
  978:                     var fragment = textMatch.Fragment;
  979:                     var lines = fragment.Split(new[] { '\n' }, StringSplitOptions.None);
  980: 
  981:                     // Find matching lines
  982:                     var matchingLineIndices = new HashSet<int>();
  983:                     for (int i = 0; i < lines.Length; i++)
  984:                     {
  985:                         if (LineContainsMatch(lines[i], query))
  986:                         {
  987:                             matchingLineIndices.Add(i);
  988:                         }
  989:                     }
  990: 
  991:                     // Determine which lines to show (matching lines + context)
  992:                     var linesToShow = new HashSet<int>();
  993:                     foreach (var matchIdx in matchingLineIndices)
  994:                     {
  995:                         // Add context lines before and after
  996:                         for (int i = Math.Max(0, matchIdx - contextLines); 
  997:                              i <= Math.Min(lines.Length - 1, matchIdx + contextLines); 
  998:                              i++)
  999:                         {
  1000:                             linesToShow.Add(i);
  1001:                         }
  1002:                     }
  1003: 
  1004:                     // Process each line, but only show lines in context window
  1005:                     for (int i = 0; i < lines.Length; i++)
  1006:                     {
  1007:                         if (!linesToShow.Contains(i))
  1008:                         {
  1009:                             continue; // Skip lines outside context window
  1010:                         }
  1011: 
  1012:                         var line = lines[i];
  1013:                         
  1014:                         // Check if this line contains a match
  1015:                         var hasMatch = matchingLineIndices.Contains(i);
  1016:                         var marker = hasMatch ? "* " : "  ";
  1017:                         
  1018:                         // For simplicity, just show line numbers starting from 1
  1019:                         // In a real implementation, we'd extract actual line numbers from GitHub
  1020:                         var lineNum = (i + 1).ToString().PadLeft(3);
  1021:                         
  1022:                         output.AppendLine($"{marker}{lineNum}: {line}");
  1023:                     }
  1024:                 }
  1025: 
  1026:                 output.AppendLine("```");
  1027:                 output.AppendLine();
  1028:             }
  1029:         }
  1030: 
  1031:         return output.ToString().TrimEnd();
  1032:     }
  1033: 
  1034:     private static bool LineContainsMatch(string line, string query)
  1035:     {
  1036:         // Simple case-insensitive search
  1037:         var keywords = query.Split(new[] { ' ' }, StringSplitOptions.RemoveEmptyEntries);
  1038:         return keywords.Any(k => line.Contains(k, StringComparison.OrdinalIgnoreCase));
  1039:     }
