  1343:     private static List<T> ApplyExcludeFilters<T>(List<T> items, List<string> excludePatterns, Func<T, string> urlGetter)
  1344:     {
  1345:         if (excludePatterns.Count == 0)
  1346:         {
  1347:             return items;
  1348:         }
  1349: 
  1350:         var filtered = items.Where(item =>
  1351:         {
  1352:             var url = urlGetter(item);
  1353:             foreach (var pattern in excludePatterns)
  1354:             {
  1355:                 try
  1356:                 {
  1357:                     if (System.Text.RegularExpressions.Regex.IsMatch(url, pattern, System.Text.RegularExpressions.RegexOptions.IgnoreCase))
  1358:                     {
  1359:                         return false; // Exclude this item
  1360:                     }
  1361:                 }
  1362:                 catch (Exception ex)
  1363:                 {
  1364:                     Logger.Warning($"Invalid regex pattern '{pattern}': {ex.Message}");
  1365:                 }
  1366:             }
  1367:             return true; // Keep this item
  1368:         }).ToList();
  1369: 
  1370:         var excludedCount = items.Count - filtered.Count;
  1371:         if (excludedCount > 0)
  1372:         {
  1373:             ConsoleHelpers.WriteLine($"Excluded {excludedCount} result(s) matching exclude pattern(s)", ConsoleColor.Yellow);
  1374:         }
  1375: 
  1376:         return filtered;
  1377:     }
