44:     private string GenerateListOutput()
45:     {
46:         var sb = new System.Text.StringBuilder();
47:         
48:         sb.AppendLine("## Chat History Conversations");
49:         sb.AppendLine();
50:         
51:         // Find all history files
52:         var files = HistoryFileHelpers.FindAllHistoryFiles();
53:         
54:         if (files.Count == 0)
55:         {
56:             sb.AppendLine("WARNING: No chat history files found");
57:             var historyDir = HistoryFileHelpers.GetHistoryDirectory();
58:             sb.AppendLine($"Expected location: {historyDir}");
59:             return sb.ToString();
60:         }
61:         
62:         // Filter by time range if After/Before are set (from --today, --yesterday, --last <timespec>, etc.)
63:         if (After.HasValue || Before.HasValue)
64:         {
65:             files = HistoryFileHelpers.FilterByDateRange(files, After, Before);
66:             
67:             if (After.HasValue && Before.HasValue)
68:             {
69:                 sb.AppendLine($"Filtered by time range: {After:yyyy-MM-dd HH:mm} to {Before:yyyy-MM-dd HH:mm} ({files.Count} files)");
70:             }
71:             else if (After.HasValue)
72:             {
73:                 sb.AppendLine($"Filtered: after {After:yyyy-MM-dd HH:mm} ({files.Count} files)");
74:             }
75:             else if (Before.HasValue)
76:             {
77:                 sb.AppendLine($"Filtered: before {Before:yyyy-MM-dd HH:mm} ({files.Count} files)");
78:             }
79:             sb.AppendLine();
80:         }
81:         // Filter by date if specified (backward compatibility)
82:         else if (!string.IsNullOrEmpty(Date))
83:         {
84:             if (DateTime.TryParse(Date, out var dateFilter))
85:             {
86:                 files = HistoryFileHelpers.FilterByDate(files, dateFilter);
87:                 sb.AppendLine($"Filtered by date: {dateFilter:yyyy-MM-dd} ({files.Count} files)");
88:                 sb.AppendLine();
89:             }
90:             else
91:             {
92:                 sb.AppendLine($"ERROR: Invalid date format: {Date}");
93:                 return sb.ToString();
94:             }
95:         }
96:         
97:         // Apply sensible default limit if not specified and no filters
98:         var effectiveLimit = Last;
99:         if (effectiveLimit == 0 && !After.HasValue && !Before.HasValue && string.IsNullOrEmpty(Date))
100:         {
101:             effectiveLimit = 20; // Default to last 20 conversations
102:             sb.AppendLine($"Showing last {effectiveLimit} conversations (use --last N to change, or --date to filter)");
103:             sb.AppendLine();
104:         }
105:         
106:         // Limit to last N if specified or defaulted
107:         if (effectiveLimit > 0 && files.Count > effectiveLimit)
108:         {
109:             files = files.Take(effectiveLimit).ToList();
110:             if (Last > 0)
111:             {
112:                 sb.AppendLine($"Showing last {effectiveLimit} conversations");
113:                 sb.AppendLine();
114:             }
115:         }
116:         
117:         // Read and display conversations
118:         var conversations = JsonlReader.ReadConversations(files);
119:         
120:         if (conversations.Count == 0)
121:         {
122:             sb.AppendLine("WARNING: No conversations could be read");
123:             return sb.ToString();
124:         }
125:         
126:         // Detect branches
127:         BranchDetector.DetectBranches(conversations);
128:         
129:         // Display conversations
130:         foreach (var conv in conversations)
131:         {
132:             var timestamp = TimestampHelpers.FormatTimestamp(conv.Timestamp, "datetime");
133:             var userCount = conv.Messages.Count(m => m.Role == "user");
134:             var assistantCount = conv.Messages.Count(m => m.Role == "assistant");
135:             var toolCount = conv.Messages.Count(m => m.Role == "tool");
136:             
137:             // Show indent if this is a branch
138:             var indent = conv.ParentId != null ? "  ↳ " : "";
139:             
140:             // Show timestamp and title
141:             sb.Append($"{indent}{timestamp} - ");
142:             
143:             if (!string.IsNullOrEmpty(conv.Metadata?.Title))
144:             {
145:                 sb.Append($"{conv.Metadata.Title} ");
146:                 sb.AppendLine($"({conv.Id})");
147:             }
148:             else
149:             {
150:                 sb.AppendLine($"{conv.Id}");
151:             }
152:             
153:             sb.AppendLine($"{indent}  Messages: {userCount} user, {assistantCount} assistant, {toolCount} tool");
154:             
155:             // Show branch info if ShowBranches is enabled
156:             if (ShowBranches)
157:             {
158:                 if (conv.ParentId != null)
159:                 {
160:                     sb.AppendLine($"{indent}  Branch of: {conv.ParentId}");
161:                 }
162:                 if (conv.BranchIds.Count > 0)
163:                 {
164:                     sb.AppendLine($"{indent}  Branches: {conv.BranchIds.Count}");
165:                 }
166:                 if (conv.ToolCallIds.Count > 0)
167:                 {
168:                     sb.AppendLine($"{indent}  Tool calls: {conv.ToolCallIds.Count}");
169:                 }
170:             }
171:             
172:             // Show preview - configurable number of messages
173:             var messageCount = MessageCount ?? 3; // Default to 3 messages
174:             var userMessages = conv.Messages.Where(m => m.Role == "user" && !string.IsNullOrWhiteSpace(m.Content)).ToList();
175:             
176:             if (userMessages.Any() && messageCount > 0)
177:             {
178:                 // For branches, show last N messages (what's new)
179:                 // For non-branches, show first N messages
180:                 var messagesToShow = conv.ParentId != null 
181:                     ? userMessages.TakeLast(Math.Min(messageCount, userMessages.Count))
182:                     : userMessages.Take(Math.Min(messageCount, userMessages.Count));
183:                 
184:                 foreach (var msg in messagesToShow)
185:                 {
186:                     var preview = msg.Content.Length > 200 
187:                         ? msg.Content.Substring(0, 200) + "..." 
188:                         : msg.Content;
189:                     preview = preview.Replace("\n", " ").Replace("\r", "");
190:                     
191:                     sb.AppendLine($"{indent}  > {preview}");
192:                 }
193:                 
194:                 // Show indicator if there are more messages
195:                 var shownCount = messagesToShow.Count();
196:                 if (userMessages.Count > shownCount)
197:                 {
198:                     sb.AppendLine($"{indent}    ... and {userMessages.Count - shownCount} more");
199:                 }
200:             }
201:             
202:             sb.AppendLine();
203:         }
204:         
205:         sb.AppendLine($"Total: {conversations.Count} conversation(s)");
206:         
207:         // Show branch statistics
208:         var branchedConvs = conversations.Count(c => c.ParentId != null);
209:         if (branchedConvs > 0)
210:         {
211:             sb.AppendLine($"Branches: {branchedConvs} conversation(s) are branches of others");
212:         }
213:         
214:         // Add statistics if requested
215:         if (ShowStats && conversations.Any())
216:         {
217:             sb.AppendLine();
218:             sb.AppendLine("═══════════════════════════════════════");
219:             sb.AppendLine("## Statistics Summary");
220:             sb.AppendLine("═══════════════════════════════════════");
221:             sb.AppendLine();
222:             
223:             var totalMessages = conversations.Sum(c => c.Messages.Count);
224:             var totalUserMessages = conversations.Sum(c => c.Messages.Count(m => m.Role == "user"));
225:             var totalAssistantMessages = conversations.Sum(c => c.Messages.Count(m => m.Role == "assistant"));
226:             var totalToolMessages = conversations.Sum(c => c.Messages.Count(m => m.Role == "tool"));
227:             var avgMessages = totalMessages / (double)conversations.Count;
