174:     private int GetDepth(Models.Conversation conv, Models.ConversationTree tree)
175:     {
176:         var depth = 0;
177:         var current = conv;
178:         while (current.ParentId != null && tree.ConversationLookup.TryGetValue(current.ParentId, out var parent))
179:         {
180:             depth++;
181:             current = parent;
182:         }
183:         return depth;
184:     }
185: 
186:     private void AppendConversationTree(System.Text.StringBuilder sb, Models.Conversation conv, Models.ConversationTree tree, int depth)
187:     {
188:         var indent = new string(' ', depth * 2);
189:         var branch = depth > 0 ? "├─ " : "📁 ";
190:         
191:         // Format timestamp
192:         var timestamp = TimestampHelpers.FormatTimestamp(conv.Timestamp, "datetime");
193:         
194:         // Show conversation
195:         var title = conv.GetDisplayTitle();
196:         var displayTitle = title.Length > 60 ? title.Substring(0, 60) + "..." : title;
197:         sb.AppendLine($"{indent}{branch}{timestamp} - {displayTitle}");
198:         
199:         // Show verbose info if requested
200:         if (Verbose)
201:         {
202:             var userCount = conv.Messages.Count(m => m.Role == "user");
203:             var assistantCount = conv.Messages.Count(m => m.Role == "assistant");
204:             
205:             sb.AppendLine($"{indent}   Messages: {userCount} user, {assistantCount} assistant");
206:             sb.AppendLine($"{indent}   Tool calls: {conv.ToolCallIds.Count}");
207:             
208:             if (conv.ParentId != null && tree.ConversationLookup.TryGetValue(conv.ParentId, out var parent))
209:             {
210:                 var commonLength = GetCommonPrefixLength(parent.ToolCallIds, conv.ToolCallIds);
211:                 var divergeAt = commonLength < parent.ToolCallIds.Count ? commonLength : parent.ToolCallIds.Count;
212:                 sb.AppendLine($"{indent}   Branched at tool call #{divergeAt}");
213:             }
214:         }
215:         
216:         // Show messages if requested
217:         var messageCount = MessageCount ?? 0; // Default to 0 for branches
218:         if (messageCount > 0)
219:         {
220:             var userMessages = conv.Messages.Where(m => m.Role == "user" && !string.IsNullOrWhiteSpace(m.Content)).ToList();
221:             
222:             if (userMessages.Any())
223:             {
224:                 // For branches, show last N messages (what's new)
225:                 // For roots, show first N messages
226:                 var messagesToShow = conv.ParentId != null 
227:                     ? userMessages.TakeLast(Math.Min(messageCount, userMessages.Count))
228:                     : userMessages.Take(Math.Min(messageCount, userMessages.Count));
229:                 
230:                 sb.AppendLine();
231:                 foreach (var msg in messagesToShow)
232:                 {
233:                     var preview = msg.Content.Length > 150 
234:                         ? msg.Content.Substring(0, 150) + "..." 
235:                         : msg.Content;
236:                     preview = preview.Replace("\n", " ").Replace("\r", "");
237:                     
238:                     sb.AppendLine($"{indent}   > {preview}");
239:                 }
240:             }
241:         }
242:         
243:         // Recursively display children  
244:         var sortedBranchIds = conv.BranchIds
245:             .Select(id => new { Id = id, Timestamp = tree.ConversationLookup.TryGetValue(id, out var tempConv) ? tempConv.Timestamp : DateTime.MinValue })
246:             .OrderBy(x => x.Timestamp)
247:             .Select(x => x.Id)
248:             .ToList();
249:             
250:         foreach (var branchId in sortedBranchIds)
251:         {
252:             if (tree.ConversationLookup.TryGetValue(branchId, out var childBranch))
253:             {
254:                 AppendConversationTree(sb, childBranch, tree, depth + 1);
255:             }
256:         }
257:     }
258: 
259:     private void AppendSingleConversationBranches(System.Text.StringBuilder sb, Models.ConversationTree tree)
260:     {
261:         // Find the conversation
262:         var conv = tree.AllConversations.FirstOrDefault(c => 
263:             c.Id.Contains(Conversation!) || c.GetDisplayTitle().Contains(Conversation!));
264:         
265:         if (conv == null)
266:         {
267:             sb.AppendLine($"ERROR: Conversation not found: {Conversation}");
268:             return;
269:         }
270:         
271:         sb.AppendLine($"## Branches for: {conv.GetDisplayTitle()}");
272:         sb.AppendLine();
273:         
274:         // Show parent chain
275:         if (conv.ParentId != null)
276:         {
277:             sb.AppendLine("Parent chain:");
278:             AppendParentChain(sb, conv, tree);
279:             sb.AppendLine();
280:         }
281:         
282:         // Show this conversation
283:         var timestamp = TimestampHelpers.FormatTimestamp(conv.Timestamp, "datetime");
284:         sb.AppendLine($"📍 {timestamp} - {conv.GetDisplayTitle()}");
285:         sb.AppendLine($"   Tool calls: {conv.ToolCallIds.Count}");
286:         sb.AppendLine();
287:         
288:         // Show children
289:         if (conv.BranchIds.Count > 0)
290:         {
291:             sb.AppendLine($"Branches ({conv.BranchIds.Count}):");
292:             foreach (var branchId in conv.BranchIds)
293:             {
294:                 if (tree.ConversationLookup.TryGetValue(branchId, out var branch))
295:                 {
296:                     var branchTimestamp = TimestampHelpers.FormatTimestamp(branch.Timestamp, "datetime");
297:                     sb.AppendLine($"  ├─ {branchTimestamp} - {branch.GetDisplayTitle()}");
298:                 }
299:             }
300:         }
301:         else
302:         {
303:             sb.AppendLine("No branches from this conversation");
304:         }
305:     }
306: 
307:     private void AppendParentChain(System.Text.StringBuilder sb, Models.Conversation conv, Models.ConversationTree tree)
308:     {
309:         var chain = new System.Collections.Generic.List<Models.Conversation>();
310:         var current = conv;
311:         
312:         while (current.ParentId != null && tree.ConversationLookup.TryGetValue(current.ParentId, out var parent))
313:         {
314:             chain.Insert(0, parent);
315:             current = parent;
316:         }
317:         
318:         for (var i = 0; i < chain.Count; i++)
319:         {
320:             var indent = new string(' ', i * 2);
321:             var timestamp = TimestampHelpers.FormatTimestamp(chain[i].Timestamp, "datetime");
322:             sb.AppendLine($"{indent}↑ {timestamp} - {chain[i].GetDisplayTitle()}");
323:         }
324:     }
325: 
326:     private int GetCommonPrefixLength(System.Collections.Generic.List<string> a, System.Collections.Generic.List<string> b)
327:     {
328:         var length = 0;
329:         var minLength = Math.Min(a.Count, b.Count);
330:         
331:         for (var i = 0; i < minLength; i++)
332:         {
333:             if (a[i] == b[i])
334:                 length++;
335:             else
336:                 break;
337:         }
338:         
339:         return length;
340:     }
341: }
