    private bool TryParseCommonInstructionOptions(CycoDjCommand command, string[] args, ref int i, string arg)
    {
        if (arg == "--instructions")
        {
            var instructions = i + 1 < args.Length ? args[++i] : null;
            if (string.IsNullOrWhiteSpace(instructions))
            {
