188:         private List<SearchMatch> SearchConversation(Models.Conversation conversation)
189:         {
190:             var matches = new List<SearchMatch>();
191:             
192:             for (int i = 0; i < conversation.Messages.Count; i++)
193:             {
194:                 var message = conversation.Messages[i];
195:                 
196:                 // Filter by role if specified
197:                 if (UserOnly && message.Role != "user") continue;
198:                 if (AssistantOnly && message.Role != "assistant") continue;
199:                 
200:                 // Skip system messages unless explicitly searching all
201:                 if (message.Role == "system" && (UserOnly || AssistantOnly)) continue;
202: 
203:                 // Search in message content
204:                 if (!string.IsNullOrWhiteSpace(message.Content))
205:                 {
206:                     var messageMatches = SearchText(message.Content);
207:                     if (messageMatches.Any())
208:                     {
209:                         matches.Add(new SearchMatch
210:                         {
211:                             MessageIndex = i,
212:                             Message = message,
213:                             MatchedLines = messageMatches
214:                         });
215:                     }
216:                 }
217:             }
218: 
219:             return matches;
220:         }
