34: 
35:             ConsoleHelpers.WriteLine($"Scanning {files.Count} conversation files...", overrideQuiet: true);
36:             ConsoleHelpers.WriteLine(overrideQuiet: true);
37: 
38:             var toRemove = new List<string>();
39: 
40:             if (FindDuplicates || RemoveDuplicates)
41:             {
42:                 toRemove.AddRange(await FindDuplicateConversationsAsync(files));
43:             }
44: 
45:             if (FindEmpty || RemoveEmpty)
46:             {
47:                 toRemove.AddRange(FindEmptyConversations(files));
48:             }
49: 
50:             if (OlderThanDays.HasValue)
51:             {
52:                 toRemove.AddRange(FindOldConversations(files, OlderThanDays.Value));
53:             }
54: 
55:             // Remove duplicates from the list
56:             toRemove = toRemove.Distinct().ToList();
57: 
58:             if (!toRemove.Any())
59:             {
60:                 ConsoleHelpers.WriteLine("✓ No files need cleanup!", ConsoleColor.Green, overrideQuiet: true);
61:                 return 0;
62:             }
63: 
64:             ConsoleHelpers.WriteLine($"Found {toRemove.Count} file(s) to clean up:", ConsoleColor.Yellow, overrideQuiet: true);
65:             ConsoleHelpers.WriteLine(overrideQuiet: true);
66: 
67:             foreach (var file in toRemove)
68:             {
69:                 var size = new FileInfo(file).Length / 1024;
70:                 ConsoleHelpers.WriteLine($"  - {Path.GetFileName(file)} ({size} KB)", ConsoleColor.DarkGray, overrideQuiet: true);
71:             }
72: 
73:             ConsoleHelpers.WriteLine(overrideQuiet: true);
74: 
75:             if (DryRun && (RemoveDuplicates || RemoveEmpty))
76:             {
77:                 ConsoleHelpers.WriteLine("DRY RUN - No files will be deleted.", ConsoleColor.Yellow, overrideQuiet: true);
78:                 ConsoleHelpers.WriteLine("Add --execute to actually remove files.", overrideQuiet: true);
79:                 return 0;
80:             }
81: 
82:             if (!DryRun && (RemoveDuplicates || RemoveEmpty))
83:             {
84:                 ConsoleHelpers.WriteLine("⚠️  WARNING: About to delete files!", ConsoleColor.Red, overrideQuiet: true);
85:                 ConsoleHelpers.Write("Type 'DELETE' to confirm: ", ConsoleColor.Yellow, overrideQuiet: true);
86:                 
