186:     private void AppendConversationTree(System.Text.StringBuilder sb, Models.Conversation conv, Models.ConversationTree tree, int depth)
187:     {
188:         var indent = new string(' ', depth * 2);
189:         var branch = depth > 0 ? "├─ " : "📁 ";
190:         
191:         // Format timestamp
192:         var timestamp = TimestampHelpers.FormatTimestamp(conv.Timestamp, "datetime");
193:         
194:         // Show conversation
195:         var title = conv.GetDisplayTitle();
196:         var displayTitle = title.Length > 60 ? title.Substring(0, 60) + "..." : title;
197:         sb.AppendLine($"{indent}{branch}{timestamp} - {displayTitle}");
198:         
199:         // Show verbose info if requested
200:         if (Verbose)
201:         {
202:             var userCount = conv.Messages.Count(m => m.Role == "user");
203:             var assistantCount = conv.Messages.Count(m => m.Role == "assistant");
204:             
205:             sb.AppendLine($"{indent}   Messages: {userCount} user, {assistantCount} assistant");
206:             sb.AppendLine($"{indent}   Tool calls: {conv.ToolCallIds.Count}");
207:             
208:             if (conv.ParentId != null && tree.ConversationLookup.TryGetValue(conv.ParentId, out var parent))
209:             {
210:                 var commonLength = GetCommonPrefixLength(parent.ToolCallIds, conv.ToolCallIds);
211:                 var divergeAt = commonLength < parent.ToolCallIds.Count ? commonLength : parent.ToolCallIds.Count;
212:                 sb.AppendLine($"{indent}   Branched at tool call #{divergeAt}");
213:             }
214:         }
215:         
216:         // Show messages if requested
217:         var messageCount = MessageCount ?? 0; // Default to 0 for branches
218:         if (messageCount > 0)
219:         {
220:             var userMessages = conv.Messages.Where(m => m.Role == "user" && !string.IsNullOrWhiteSpace(m.Content)).ToList();
221:             
222:             if (userMessages.Any())
223:             {
224:                 // For branches, show last N messages (what's new)
225:                 // For roots, show first N messages
226:                 var messagesToShow = conv.ParentId != null 
227:                     ? userMessages.TakeLast(Math.Min(messageCount, userMessages.Count))
228:                     : userMessages.Take(Math.Min(messageCount, userMessages.Count));
229:                 
230:                 sb.AppendLine();
231:                 foreach (var msg in messagesToShow)
232:                 {
233:                     var preview = msg.Content.Length > 150 
234:                         ? msg.Content.Substring(0, 150) + "..." 
235:                         : msg.Content;
236:                     preview = preview.Replace("\n", " ").Replace("\r", "");
237:                     
238:                     sb.AppendLine($"{indent}   > {preview}");
239:                 }
240:             }
241:         }
242:         
243:         // Recursively display children  
244:         var sortedBranchIds = conv.BranchIds
245:             .Select(id => new { Id = id, Timestamp = tree.ConversationLookup.TryGetValue(id, out var tempConv) ? tempConv.Timestamp : DateTime.MinValue })
246:             .OrderBy(x => x.Timestamp)
247:             .Select(x => x.Id)
248:             .ToList();
249:             
250:         foreach (var branchId in sortedBranchIds)
251:         {
252:             if (tree.ConversationLookup.TryGetValue(branchId, out var childBranch))
253:             {
254:                 AppendConversationTree(sb, childBranch, tree, depth + 1);
255:             }
256:         }
257:     }
