  46:     {
  47:         if (string.IsNullOrEmpty(timeSpec))
  48:             throw new CommandLineException($"Missing time specification for {optionName}");
  49: 
  50:         // Handle special keywords
  51:         if (timeSpec.Equals("today", StringComparison.OrdinalIgnoreCase))
  52:             return isAfter ? DateTime.Today : DateTime.Today.AddDays(1).AddTicks(-1);
  53: 
  54:         if (timeSpec.Equals("yesterday", StringComparison.OrdinalIgnoreCase))
  55:             return isAfter ? DateTime.Today.AddDays(-1) : DateTime.Today.AddTicks(-1);
  56: 
  57:         // Handle absolute dates
  58:         if (DateTime.TryParse(timeSpec, out var dateTime))
  59:         {
  60:             // If it's a date without time, adjust to start/end of day as appropriate
  61:             if (IsDateOnly(dateTime))
  62:             {
  63:                 return isAfter ? dateTime : dateTime.AddDays(1).AddTicks(-1);
  64:             }
  65:             return dateTime;
  66:         }
